/*СТАТЬИ                       */
/*ВИД ЗАТРАТ                    */
/*ПЕРИОД                       */@ILDESCENDANTS({СПИСОК_ПЕРИОД}) and @RELATIVE("Всего год",0)
)

/*Расчет Оплата труда*/

FIX(
/*СЧЕТА БАЛАНСА                 */"20_Начисление","26_Поступление","29_Поступление"
/*СТАТЬИ                       */"Оплата труда.","Резерв на отпуск."
)
FIX(
/*МВЗ                          */@Relative({МВЗ},0)
/*ПРОДУКЦИЯ                    */
/*ВИД ЗАТРАТ                    */"Страховые взносы","ФОТ.","Резерв на отпуск"
)



FIX(
/*ПРОДУКЦИЯ                    */@RELATIVE("Всего по продукции",0)
/*ПОКАЗАТЕЛЬ                   */Сумма,"Кол-во"
/*ЦФО                        */"УТиП"
)

"Прочие внешние"(
IF(
NOT @ISMBR("Ремонтно-механический Уч"->"Автоматическая наплавка в целом")
and NOT @ISMBR("Ремонтно-механический Уч"->"Поковка в целом")
and NOT (@ISMBR("Ремонтно-механический Уч") and @ISDESC("Напыление порошка"))
and NOT (@ISMBR("Литейный Уч") and @ISDESC("Жидкая сталь на литье.")) /*распределяется с в целом на конечные продукты*/
and NOT (@ISMBR("Литейный Уч") and @ISDESC("Стальное литье"))/*распределяется с в целом на конечные продукты*/

) /*исключаем задвоение*/
  IF("Прочие внешние"!=#missing)
     IF(@ISMBR("Страховые взносы"->"Оплата труда."))
        "ПЭО"->"бу контрагента" ="Прочие внешние"-"Ремонты сами себе"->"бу контрагента"->"бу направления"->"Всего по группам ОС и НМА"->"ПЭО"->"Страховые взносы на ремонты"->"Затраты на ремонт";
     ELSEIF(@ISMBR("ФОТ."->"Оплата труда."))
        "ПЭО"->"бу контрагента" ="Прочие внешние"-"Ремонты сами себе"->"бу контрагента"->"бу направления"->"Всего по группам ОС и НМА"->"ПЭО"->"ФОТ на ремонты"->"Затраты на ремонт";
     ELSEIF(@ISMBR("Резерв на отпуск"->"Резерв на отпуск."))
        "ПЭО"->"бу контрагента" ="Прочие внешние"-"Ремонты сами себе"->"бу контрагента"->"бу направления"->"Всего по группам ОС и НМА"->"ПЭО"->"Резерв на отпуск на ремонт сами себе"->"Затраты на ремонт";
  ENDIF
ENDIF
ENDIF) 

ENDFIX
FIX(
/*ЦФО                        */ПЭО
/*КОНТРАГЕНТЫ                    */"бу контрагента"
/*ПРОДУКЦИЯ                    */"бу вида продукции"
)

SET CREATENONMISSINGBLK ON;
"Сумма"(
IF(NOT @ISMBR("Работа локомотива_"))
IF((@ISMBR(@LIST("№9. Цех связи","№11. АСС","№13. ЦЛХТиТК","№20. Управление","№22. ИТЦ","№25. ЦЛА","№26. Хоз цех","№29. ЦОП","Управление")) and @ISMBR("26_Поступление"))
or (@ISMBR(@LIST("№23. Цех питания","БО Металлург.","ФОК","Спортивно-стрелковый стенд")) and @ISMBR("29_Поступление"))
or (NOT @ISMBR(@LIST("№9. Цех связи","№11. АСС","№13. ЦЛХТиТК","№20. Управление","№22. ИТЦ","№25. ЦЛА","№26. Хоз цех","№29. ЦОП","Управление","Ремонтный цех","№23. Цех питания","БО Металлург.","ФОК","Спортивно-стрелковый стенд")) and @ISMBR("20_Начисление"))
)
    IF("Сумма"->"Итого по продукции"==#missing) 
      IF(@ISMBR("Страховые взносы"->"Оплата труда."))
        	 "Сумма"="УТиП"->"Прочие внешние"->"Сумма"-"Ремонты сами себе"->"бу контрагента"->"бу направления"->"Всего по группам ОС и НМА"->"ПЭО"->"Страховые взносы на ремонты"->"Затраты на ремонт"->"Сумма";
        ELSEIF(@ISMBR("ФОТ."->"Оплата труда."))
        	 "Сумма"="УТиП"->"Прочие внешние"->"Сумма"-"Ремонты сами себе"->"бу контрагента"->"бу направления"->"Всего по группам ОС и НМА"->"ПЭО"->"ФОТ на ремонты"->"Затраты на ремонт"->"Сумма";
  		ELSEIF(@ISMBR("Резерв на отпуск"->"Резерв на отпуск."))
			"Сумма"="УТиП"->"Прочие внешние"->"Сумма"-"Ремонты сами себе"->"бу контрагента"->"бу направления"->"Всего по группам ОС и НМА"->"ПЭО"->"Резерв на отпуск на ремонт сами себе"->"Затраты на ремонт"->"Сумма";      		ENDIF
      ENDIF

   ENDIF   
 ENDIF)
SET CREATENONMISSINGBLK OFF;

CLEARBLOCK EMPTY;
ENDFIX
ENDFIX


ENDFIX
ENDFIX
