FIX(
/*МВЗ ПОЛУЧАТЕЛЬ                                    */@REMOVE(@RELATIVE(@MEMBER(@CONCATENATE(@NAME({МВЗ})," (пол-ль)")),0),@LIST("ТЧМ в целом (пол-ль)","ТЧМС в целом (пол-ль)") )/*исключаем суммы тупиковой ветки*/
/*МВЗ                                               */@RELATIVE({МВЗ},0)
/*ПОКАЗАТЕЛИ                                        */"Сумма","Драйвер"
)

