/*ПРОДУКЦИЯ											*/"ПРОДУКЦИЯ ТЧМ и ТЧМС"
)

FIXPARALLEL(8,@DESCENDANTS("Всего по видам затрат (пол-ль)"))

AGG("МВЗ ПОЛУЧАТЕЛЬ");
"МВЗ ПОЛУЧАТЕЛИ"(
	IF(@ISDESC("ТЧМ Итого"))
		"МВЗ ПОЛУЧАТЕЛИ"= "МВЗ ПОЛУЧАТЕЛИ"-"ТЧМ в целом (пол-ль)";
	ELSE
		"МВЗ ПОЛУЧАТЕЛИ"= "МВЗ ПОЛУЧАТЕЛИ"-"ТЧМС в целом (пол-ль)";
ENDIF)


ENDFIXPARALLEL

ENDFIX

