rule.script:
SET AGGMISSG ON;
SET UPDATECALC OFF;
SET CREATEBLOCKONEQ OFF;
SET CREATENONMISSINGBLK OFF;
SET FRMLBOTTOMUP OFF;
SET REMOTECALC OFF;
SET CALCPARALLEL 1;
SET CALCTASKDIMS 1;
SET EMPTYMEMBERSETS ON;
SET FRMLRTDYNAMIC OFF;
SET MSG INFO;
/* Проверка корректности рассчитываемого среза данных */

FIX(
/*ВИД ЗАТРАТ ПОЛУЧАТЕЛЬ				*/  "бу видов затрат (пол-ль)"
/*ПРОДУКТ ПОЛУЧАТЕЛЬ				*/  "бу вида продукции (пол-ль)"
/* ВАЛЮТА							*/	"бу валюты"
/* ПЕРИОД							*/	"P0"
/* ГОД								*/	"No Year"
/* ВЕРСИЯ							*/	
/* СЦЕНАРИЙ							*/	
/* ЦФО								*/	"бу ЦФО"
/* МВЗ								*/	"бу МВЗ"
/* СЧЕТА БАЛАНСА					*/	"20_Начисление"
/* СТАТЬИ							*/	
/* ПОКАЗАТЕЛИ						*/	"Сумма"
/* ВИД ЗАТРАТ						*/	"бу видов затрат"
/* КОНТРАГЕНТЫ						*/	"бу контрагента"
/* НАПРАВЛЕНИЕ	*/	"бу направления"
/* ЭЛЕМЕНТЫ_ЗАТРАТ					*/	"бу элемента затрат"
/* ПРОЧЕЕ							*/	"бу прочих справочников"

)
  SET CREATENONMISSINGBLK ON;


  var ErrFlagScenario=0;
  var ErrFlagVersion=0;

  /* ПРОВЕРКА КОРРЕКТНОСТИ ВЫБОРА СЦЕНАРИЯ */
  FIX(/* Сценарий */ {СЦЕНАРИЙ} AND @LIST(&ОТКРЫТЫЕ_СЦЕНАРИИ))
    FIX(
    /* Сценарий */ 
    /* Версия   */ "Рабочая 1"
    )
     "бу статьи" (
     IF (ErrFlagScenario==0)
            ErrFlagScenario=1;
     ENDIF
     );

    ENDFIX
  ENDFIX

   /* ПРОВЕРКА КОРРЕКТНОСТИ ВЫБОРА ВЕРСИИ */
  FIX(/* Версия  */ {ВЕРСИЯ} AND @LIST(&ОТКРЫТЫЕ_ВЕРСИИ))
    FIX(
    /* Сценарий */ "Проект"
    /* Версия   */ 
    )
     "бу статьи" (
     IF (ErrFlagVersion==0)
            ErrFlagVersion=1;
     ENDIF
     );
    ENDFIX
  ENDFIX

  /* ВЫВОД СООБЩЕНИЙ О НЕКОРРЕКТНОМ СРЕЗЕ */
  FIX(
  /* Сценарий     */ "Проект"
  /* Версия       */ "Рабочая 1"
  )

   "бу статьи" (
    IF (ErrFlagScenario==0)
       @RETURN("Выбран сценарий, закрытый для расчета. Расчет не произведен.",ERROR);
    ENDIF
    IF (ErrFlagVersion==0)
       @RETURN("Выбрана версия, закрытая для расчета. Расчет не произведен.",ERROR);
    ENDIF
    )

   ENDFIX
ENDFIX
SET CREATENONMISSINGBLK OFF;
FIX ( 
/* ВИД ЗАТРАТ ПОЛУЧАТЕЛЬ			*/"бу видов затрат (пол-ль)"
/* ПРОДУКЦИЯ ПОЛУЧАТЕЛЬ           	*/"бу вида продукции (пол-ль)"
/* ВАЛЮТА							*/"бу валюты"
/* ВЕРСИЯ                         	*/"Рабочая 1" 
/* СЦЕНАРИЙ                     	*/"Проект"
/* ПЕРИОД                         	*/"P0"
/* ПОКАЗАТЕЛИ                       */"Кол-во"
/* СЧЕТА БАЛАНСА                   	*/@ILDESCENDANTS({СЧЕТА_БАЛАНСА})
/* ЦФО                            	*/"ПЭО"
/* КОНТРАГЕНТЫ                      */"бу контрагента"
/* ГОД                            	*/
/* СТАТЬИ                         	*/@ILDESCENDANTS({СПИСОК_СТАТЕЙ}) and @RELATIVE("Статьи калькуляции",0)
/* НАПРАВЛЕНИЕ    					*/"бу направления"
/* ПРОДУКЦИЯ                      	*/"бу вида продукции"
/* ПРОЧЕЕ                         	*/"бу прочих справочников"
/* ЭЛЕМЕНТЫ_ЗАТРАТ                 	*/"бу элемента затрат"
/* МВЗ ПОЛУЧАТЕЛЬ                   */"бу МВЗ (пол-ль)"
/* ВИД ЗАТРАТ                      	*/@ILDESCENDANTS({СПИСК_ВИДОВ_ЗАТРАТ}) and @RELATIVE("Итого по видам затрат",0)
/* МВЗ                            	*/@ILDESCENDANTS({СПИСОК_МВЗ}) and @RELATIVE("Итого по МВЗ",0)
)
/*Удаляем ВЗ в мэппинг для МВЗ*/
{ГОД}=#MISSING;

ENDFIX

FIX ( 
/* ВИД ЗАТРАТ ПОЛУЧАТЕЛЬ			*/"бу видов затрат (пол-ль)"
/* ПРОДУКЦИЯ ПОЛУЧАТЕЛЬ           	*/"бу вида продукции (пол-ль)"
/* ВАЛЮТА							*/"бу валюты"
/* ВЕРСИЯ                         	*/"Рабочая 1" 
/* СЦЕНАРИЙ                     	*/"Проект"
/* ПЕРИОД                         	*/"P0"
/* ПОКАЗАТЕЛИ                       */"Кол-во"
/* СЧЕТА БАЛАНСА                   	*/@ILDESCENDANTS({СЧЕТА_БАЛАНСА})
/* ЦФО                            	*/"ПЭО"
/* КОНТРАГЕНТЫ                      */"бу контрагента"
/* ГОД                            	*/ {ГОД}
/* СТАТЬИ                         	*/@ILDESCENDANTS({СПИСОК_СТАТЕЙ}) and @RELATIVE("Статьи калькуляции",0)
/* НАПРАВЛЕНИЕ    					*/"бу направления"
/* ПРОДУКЦИЯ                      	*/"бу вида продукции"
/* ПРОЧЕЕ                         	*/"бу прочих справочников"
/* ЭЛЕМЕНТЫ_ЗАТРАТ                 	*/"бу элемента затрат"
/* МВЗ ПОЛУЧАТЕЛЬ                   */"бу МВЗ (пол-ль)"
/* ВИД ЗАТРАТ                      	*/
/* МВЗ                            	*/@ILDESCENDANTS({СПИСОК_МВЗ}) and @RELATIVE("Итого по МВЗ",0)
)
/*Агрегируем */
AGG("ВИД ЗАТРАТ");

ENDFIX
