rule.script:
SET AGGMISSG ON;
SET UPDATECALC OFF;
SET CREATEBLOCKONEQ OFF;
SET CREATENONMISSINGBLK OFF;
SET FRMLBOTTOMUP OFF;
SET REMOTECALC OFF;
SET CALCPARALLEL 1;
SET CALCTASKDIMS 1;
SET EMPTYMEMBERSETS ON;
SET FRMLRTDYNAMIC OFF;
SET MSG INFO;
/* Проверка корректности рассчитываемого среза данных */

FIX(
/* ВАЛЮТА							*/	"бу валюты"
/* ПЕРИОД							*/	"P0"
/* ГОД								*/	"No Year"
/* ВЕРСИЯ							*/	
/* СЦЕНАРИЙ							*/	
/* ЦФО								*/	"бу ЦФО"
/* МВЗ								*/	"бу МВЗ"
/* МВЗ ПОЛУЧАТЕЛЬ					*/	"бу МВЗ (пол-ль)"
/* СЧЕТА БАЛАНСА					*/	"80_Увеличение"
/* СТАТЬИ							*/	
/* ПОКАЗАТЕЛИ						*/	"Сумма"
/* ВИД ЗАТРАТ						*/	"бу видов затрат"
/* ПРОДУКЦИЯ						*/	"бу вида продукции"
/* КОНТРАГЕНТЫ						*/	"бу контрагента"
/* НАПРАВЛЕНИЕ	*/	"бу направления"
/* ЭЛЕМЕНТЫ_ЗАТРАТ					*/	"бу элемента затрат"
/* ПРОЧЕЕ							*/	"бу прочих справочников"

)
  SET CREATENONMISSINGBLK ON;


  var ErrFlagScenario=0;
  var ErrFlagVersion=0;
  var NextM=2;
  /* ПРОВЕРКА КОРРЕКТНОСТИ ВЫБОРА СЦЕНАРИЯ */
  FIX(/* Сценарий */ {СЦЕНАРИЙ} AND @LIST(&ОТКРЫТЫЕ_СЦЕНАРИИ))
    FIX(
    /* Сценарий */ 
    /* Версия   */ "Рабочая 1"
    )
     "бу статьи" (
     IF (ErrFlagScenario==0)
            ErrFlagScenario=1;
     ENDIF
     );

    ENDFIX
  ENDFIX

   /* ПРОВЕРКА КОРРЕКТНОСТИ ВЫБОРА ВЕРСИИ */
  FIX(/* Версия  */ {ВЕРСИЯ} AND @LIST(&ОТКРЫТЫЕ_ВЕРСИИ))
    FIX(
    /* Сценарий */ "Проект"
    /* Версия   */ 
    )
     "бу статьи" (
     IF (ErrFlagVersion==0)
            ErrFlagVersion=1;
     ENDIF
     );
    ENDFIX
  ENDFIX

  /* ВЫВОД СООБЩЕНИЙ О НЕКОРРЕКТНОМ СРЕЗЕ */
  FIX(
  /* Сценарий     */ "Проект"
  /* Версия       */ "Рабочая 1"
  )

   "бу статьи" (
    IF (ErrFlagScenario==0)
       @RETURN("Выбран сценарий, закрытый для расчета. Расчет не произведен.",ERROR);
    ENDIF
    IF (ErrFlagVersion==0)
       @RETURN("Выбрана версия, закрытая для расчета. Расчет не произведен.",ERROR);
    ENDIF
    )

   ENDFIX
ENDFIX
SET CREATENONMISSINGBLK OFF;
FIX(
/*ЭЛЕМЕНТЫ_ЗАТРАТ					 */"бу элемента затрат"
/*ЦФО 						  		 */"ПЭО"
/*КОНТРАГЕНТЫ 						 */"бу контрагента"
/*НАПРАВЛЕНИЕ 	 				     */"бу направления"
/*ПРОЧЕЕ 							 */@RELATIVE("Всего по прочим справочникам в смете",0)
/*СЧЕТА БАЛАНСА                      */
/*МВЗ ПОЛУЧАТЕЛЬ 					 */"бу МВЗ (пол-ль)",@RELATIVE(@MEMBER(@CONCATENATE(@NAME({МВЗ})," (пол-ль)")),0)
/*ВАЛЮТА 							 */"RUR"
/*ВИД ЗАТРАТ 						 */@RELATIVE("Всего по видам затрат",0)
/*СТАТЬИ							 */@RELATIVE("ПОЛНАЯ СЕБЕСТОИМОСТЬ",0)
/*СЦЕНАРИЙ							 */{СЦЕНАРИЙ}
/*ПЕРИОД							 */@ILDESCENDANTS({СПИСОК_ПЕРИОД}) and @RELATIVE("Всего год",0)
/*ВЕРСИЯ 							 */{ВЕРСИЯ}
/*ГОД 								 */{ГОД}
/*ПРОДУКЦИЯ 						 */@RELATIVE("Всего по продукции",0)
/*ПОКАЗАТЕЛИ 						 */"Сумма","Кол-во"
/*МВЗ 								 */@REMOVE(@RELATIVE({МВЗ},0),@LIST("№1. Аглоцех","Грузоперевозки_", "№20. Управление","Ремонтный цех", "Лаборатория"))
)
FIX(
/*СЧЕТА БАЛАНСА                      */"20_Исключение ВЦО"
)
CLEARBLOCK ALL;
ENDFIX




ENDFIX
/* Общий подход исключения по ВЗ, сформированным из Настроек калькулятора, по Продукции, преобразованной в ВЗ  */
FIX(
/*ЭЛЕМЕНТЫ_ЗАТРАТ					 */
/*ЦФО 						  		 */"ПЭО"
/*КОНТРАГЕНТЫ 						 */"бу контрагента"
/*НАПРАВЛЕНИЕ 	 				     */"бу направления"
/*ПРОЧЕЕ 							 */
/*СЧЕТА БАЛАНСА                      */"20_Начисление"
/*МВЗ ПОЛУЧАТЕЛЬ 					 */"бу МВЗ (пол-ль)",@RELATIVE(@MEMBER(@CONCATENATE(@NAME({МВЗ})," (пол-ль)")),0)
/*ВАЛЮТА 							 */"RUR"
/*ВИД ЗАТРАТ 						 */@RELATIVE("Всего по видам затрат",0)
/*СТАТЬИ							 */@RELATIVE("ПОЛНАЯ СЕБЕСТОИМОСТЬ",0)
/*СЦЕНАРИЙ							 */{СЦЕНАРИЙ}
/*ПЕРИОД							 */@ILDESCENDANTS({СПИСОК_ПЕРИОД}) and @RELATIVE("Всего год",0)
/*ВЕРСИЯ 							 */{ВЕРСИЯ}
/*ГОД 								 */{ГОД}
/*ПРОДУКЦИЯ 						 */@RELATIVE("Всего по продукции",0)
/*ПОКАЗАТЕЛИ 						 */
/*МВЗ 								 */@REMOVE(@RELATIVE({МВЗ},0),@LIST("№1. Аглоцех","Грузоперевозки_", "№20. Управление","Ремонтный цех", "Лаборатория"))
)
    FIX(
    /*ЭЛЕМЕНТЫ_ЗАТРАТ					 */"бу элемента затрат"
    /*ПРОЧЕЕ 							 */@RELATIVE("Всего по прочим справочникам в смете",0)
    )

        "Сумма"(
        /*Проверяем маркер на ВЗ, пришедший из Настройки калькулятора и преобразованный из Продукции. Смотрим на родителя МВЗ*/
       IF ("Рабочая_1"->"Проект"->"бу прочих справочников"->"20_Исключение ВЦО"->"бу валюты"->"бу статьи"->"P0"->"Сумма"->"бу МВЗ (пол-ль)"->"бу вида продукции"->@MEMBER(@ANCEST(@CURRMBR("МВЗ"),5))+0 != 0) 
          	"Кол-во"->"20_Исключение ВЦО" = "Кол-во";
           	"Сумма"->"20_Исключение ВЦО"  ="Сумма";
       ENDIF
        )
    ENDFIX
ENDFIX
