@XWRITE("Сумма"->@MEMBER(@SUBSTRING(@NAME("Отсев агломерата снятие (первич.)"),0,@CalcMgrTextLength(@NAME("Отсев агломерата снятие (первич.)"))-10)),@LOOPBACK,"Сумма",@SUBSTRING(@NAME("Отсев агломерата снятие (первич.)"),0,@CalcMgrTextLength(@NAME("Отсев агломерата снятие (первич.)"))-10),"Отсев агломерата снятие (первич.)");
