/*Переносим ВЗ на ЭЗ*/

FIX(
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */"ЭЛЕМЕНТЫ ЗАТРАТ ТЧМ И ТЧМС" @RELATIVE("Всего по первичным элементам затрат.",0)
/*ЦФО                            */"ПЭО"
/*КОНТРАГЕНТЫ                    */"бу контрагента"/*костылек дляч продукции упр мвз*/,@Relative("Всего по контрагентам", 0)
/*НАПРАВЛЕНИЕ                    */"бу направления"/*костылек дляч продукции упр мвз*/,"Тов. пр-ция (сч 43)"
/*ПРОЧЕЕ                           */@RELATIVE("Всего по прочим справочникам в смете",0),@Relative("Всего по уровням бюджета", 0)
/*СЧЕТА БАЛАНСА                   */@RELATIVE("Начисление",0)
/*МВЗ ПОЛУЧАТЕЛЬ                 */"бу МВЗ (пол-ль)",@RELATIVE(@MEMBER(@CONCATENATE(@NAME({МВЗ})," (пол-ль)")),0)   
/*ВАЛЮТА                         */"RUR"
/*ВИД ЗАТРАТ                     */@RELATIVE("Всего по видам затрат",0)
/*СТАТЬИ                        */@RELATIVE("ЦЕХОВАЯ СЕБЕСТОИМОСТЬ",0),"КОММЕРЧЕСКИЕ РАСХОДЫ"
/*СЦЕНАРИЙ                       */{СЦЕНАРИЙ}
/*ПЕРИОД                         */@ILDESCENDANTS({СПИСОК_ПЕРИОД}) and @RELATIVE("Всего год",0)
/*ВЕРСИЯ                          */{ВЕРСИЯ}
/*ГОД                              */{ГОД}
/*ПРОДУКЦИЯ                       */
/*ПОКАЗАТЕЛИ                       */
/*МВЗ                             */@RELATIVE({МВЗ},0)
)
FIXPARALLEL(8,@DESCENDANTS("Итого по продукции"))
"Сумма"(
      IF(@ISMBR(@CURRMBR("ВИД_ЗАТРАТ")) and not @ISMBR(@MEMBER(@CONCATENATE(@NAME(@CURRMBR("ВИД_ЗАТРАТ"))," (первич.)")) ) )
      "Сумма"=#missing;
      "Кол-во"=#missing;
/*дополнительно делаем зачистку для ВЗ которые у ТЧМ внутренние*/
      ELSEIF(@ISDESC("ТЧМ Итого") and @ISMBR(@LIST("Азот газообразный","Кислород технологический","Питьевая вода","Техническая вода")))       
      "Сумма"=#missing;
      "Кол-во"=#missing;
ENDIF;      )


ENDFIXPARALLEL

/*для агломерата скипового убираем со статьи отходы ВЗ Коксовая мелочь с печей и Коксовая мелочь с рво
это кол-во ВЗ добавлено в калькуляцию искуственно для отображения на форме и отношения к себестоимости не имеет
*/
FIX(
/*ПРОДУКЦИЯ                       */"Агломерат бункерный"
)

"Кол-во"(
IF(@ISMBR("Коксовая мелочь с печей", "Коксовая мелочь с РВО","Отсев кокса мелкофракционный АС")
	and @ISMBR("Отходы производства") 
    and @ISMBR("Коксовая мелочь с печей (первич.)", "Коксовая мелочь с РВО (первич.)","Отсев кокса мелкофракционный АС (первич.)"))
	"Кол-во" = #missing;
ENDIF)

ENDFIX 

ENDFIX
/*костыль для агломерата скипового
агломерат скиповый входит в состав продуктов группы МПЗ, которые закрываются в ом числе и внутри расчета калькулятор
для того что бы избежать наличия агломерата как ЭЗ в МПЗ, заменяем его на сырье , себестомость котрых покрывает 90% от всей суммы 
*/

FIX(
/*МВЗ                             */@RELATIVE({МВЗ},0) and "Литейный Уч"
)

