)



FIXPARALLEL(8,@DESCENDANTS("Итого по продукции"))

/*ремонт введен на продукт */
FIX(
/*ПРОЧЕЕ                           */@RELATIVE("Всего по группам ОС и НМА",0)
/*МВЗ ПОЛУЧАТЕЛЬ                 */"бу МВЗ (пол-ль)"
/*ВИД ЗАТРАТ                     */"Ремонты сами себе" 
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */"ФОТ на ремонты","Страховые взносы на ремонты","Резерв на отпуск на ремонт сами себе","Амортизация на ремонты" 

)
"Сумма"(
      IF(@ISMBR("ФОТ на ремонты"))
         "Сумма"->"ФОТ. (первич.)" = "Сумма";
        ELSEIF(@ISMBR("Страховые взносы на ремонты"))
         "Сумма"->"Страховые взносы (первич.)" = "Сумма";
        ELSEIF(@ISMBR( "Резерв на отпуск на ремонт сами себе"))
         "Сумма"->"Резерв на отпуск (первич.)" = "Сумма";

        ELSEIF(@ISMBR("Амортизация на ремонты"))
         "Сумма"->"Амортизация ОС и НМА (первич.)" =  "Сумма";
      ENDIF;
        )
ENDFIX 

/*ремонт распределен на продукт */
FIX(
/*ПРОЧЕЕ                           */"бу прочих справочников"
/*МВЗ ПОЛУЧАТЕЛЬ                 */@RELATIVE(@MEMBER(@CONCATENATE(@NAME({МВЗ})," (пол-ль)")),0)  
/*ВИД ЗАТРАТ                     */"Ремонты сами себе" 
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */"Ремонты сами себе (первич.)"

)
"Сумма"(
         "Сумма"->"ФОТ. (первич.)" = "Сумма"*
