	IF(@ISMBR("НЗП на начало периода"))
    	"Сумма"->"январь" + @SUMRANGE("Доля %"->"НЗП на конец периода",@LIST("Январь": @CURRMBR("Период"))) -"Доля %"->"НЗП на конец периода";
	ELSE
