Сумма(
	IF(("Трансформация Пр в ВЗ"==3 or ("Трансформация Пр в ВЗ">9 and "Трансформация Пр в ВЗ"<14) or @ISMBR("Мех.обработка изделий.") /*Мех.обработка изделий. на прямую не является МПЗ, но 90% ее уходит в МПЗ.. по этому включаем ее для расчета структуры ЭЗ МПЗ*/
    ) and Сумма > 0) /*признак перекладки Продукта на МПЗ собственного производства в целом*/
		@XWRITE("Сумма",@LOOPBACK,"Сумма","МПЗ в целом");
        @XWRITE("Кол-во",@LOOPBACK,"Кол-во","МПЗ в целом");
	ENDIF
)
