/*расшиваем сумму в пропорции тех среза */
FIX(
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */@RELATIVE("Всего по первичным элементам затрат.",0)
/*ЦФО                            */"ПЭО"
/*КОНТРАГЕНТЫ                    */"бу контрагента"
/*НАПРАВЛЕНИЕ                    */"бу направления"
/*ПРОЧЕЕ                       	 */"бу прочих справочников"
/*СЧЕТА БАЛАНСА               	 */@RELATIVE("Начисление",0)
/*МВЗ ПОЛУЧАТЕЛЬ                 */"бу МВЗ (пол-ль)",@RELATIVE(@MEMBER(@CONCATENATE(@NAME({МВЗ})," (пол-ль)")),0)   
/*ВАЛЮТА                         */"RUR"
/*ВИД ЗАТРАТ                     */"МПЗ собственного производства в целом"
/*СТАТЬИ                        */@RELATIVE("ЦЕХОВАЯ СЕБЕСТОИМОСТЬ",0)
/*СЦЕНАРИЙ                       */{СЦЕНАРИЙ}
/*ПЕРИОД                         */@ILDESCENDANTS({СПИСОК_ПЕРИОД}) and @RELATIVE("Всего год",0)
/*ВЕРСИЯ                          */{ВЕРСИЯ}
/*ГОД                              */{ГОД}
/*ПРОДУКЦИЯ                       */
/*ПОКАЗАТЕЛИ                       */
/*МВЗ                             */
)

FIXPARALLEL(8,@DESCENDANTS("Итого по продукции"))

"Сумма"(
		IF("Сумма"->"бу элемента затрат"+0!=0)

"Сумма" = "Сумма"->"бу элемента затрат"*
 		 "Сумма"->"ЦЕХОВАЯ СЕБЕСТОИМОСТЬ"->"МВЗ получатели"->"ПРОДУКЦИЯ ТЧМ и ТЧМС"->"МПЗ в целом"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Прочие МВЗ"
 	 	/"Сумма"->"ЦЕХОВАЯ СЕБЕСТОИМОСТЬ"->"МВЗ получатели"->"ПРОДУКЦИЯ ТЧМ и ТЧМС"->"МПЗ в целом"->"Технически элемент"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Прочие МВЗ";

"Кол-во" =
		/*доля списанной себ-ти всех МПЗ */
		"Сумма"->"бу элемента затрат"
 	 	/"Сумма"->"ЦЕХОВАЯ СЕБЕСТОИМОСТЬ"->"МВЗ получатели"->"ПРОДУКЦИЯ ТЧМ и ТЧМС"->"МПЗ в целом"->"Технически элемент"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Прочие МВЗ"

		/*кол-во каждого из ЭЗ всех МПЗ */
		*"Кол-во"->"ЦЕХОВАЯ СЕБЕСТОИМОСТЬ"->"МВЗ получатели"->"ПРОДУКЦИЯ ТЧМ и ТЧМС"->"МПЗ в целом"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Прочие МВЗ"
        ;

      ELSE 
"Сумма" =#missing;
"Кол-во" =#missing;
ENDIF
)

ENDFIXPARALLEL
ENDFIX 
ENDFIX
FIX(
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */
/*ЦФО                            */"ПЭО"
/*КОНТРАГЕНТЫ                    */"бу контрагента"
/*НАПРАВЛЕНИЕ                    */"бу направления"
/*ПРОЧЕЕ                       	 */"бу прочих справочников"
/*СЧЕТА БАЛАНСА               	 */"МПЗ в целом"
/*МВЗ ПОЛУЧАТЕЛЬ                 */   
/*ВАЛЮТА                         */"RUR"
/*ВИД ЗАТРАТ                     */
/*СТАТЬИ                        */@RELATIVE("ЦЕХОВАЯ СЕБЕСТОИМОСТЬ",0)
/*СЦЕНАРИЙ                       */{СЦЕНАРИЙ}
/*ПЕРИОД                         */@ILDESCENDANTS({СПИСОК_ПЕРИОД}) and @RELATIVE("Всего год",0)
/*ВЕРСИЯ                          */{ВЕРСИЯ}
/*ГОД                              */{ГОД}
/*ПРОДУКЦИЯ                       */
/*ПОКАЗАТЕЛИ                       */"Сумма","Кол-во"
/*МВЗ                             */
)
FIXPARALLEL(8,@DESCENDANTS("Всего по первичным элементам затрат."))

FIX(
/*МВЗ                             */@RELATIVE({МВЗ},0),"Прочие МВЗ"
/*ВИД ЗАТРАТ                     */@RELATIVE("Всего по видам затрат",0),"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"
/*МВЗ ПОЛУЧАТЕЛЬ                 */"МВЗ получатели","бу МВЗ (пол-ль)",@RELATIVE(@MEMBER(@CONCATENATE(@NAME({МВЗ})," (пол-ль)")),0)   
/*ПРОДУКЦИЯ                       */"ПРОДУКЦИЯ ТЧМ и ТЧМС",@RELATIVE("Итого по продукции",0)
)
CLEARBLOCK ALL;
ENDFIX

ENDFIXPARALLEL


FIX(
/*МВЗ                             */"Прочие МВЗ"
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */"Технически элемент",@RELATIVE("Всего по первичным элементам затрат.",0)
/*ВИД ЗАТРАТ                     */"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"
/*МВЗ ПОЛУЧАТЕЛЬ                 */"МВЗ получатели"
/*ПРОДУКЦИЯ                       */"ПРОДУКЦИЯ ТЧМ и ТЧМС"
)
CLEARBLOCK ALL;
ENDFIX

ENDFIX
/*АГРЕГИРУЕМ */
FIX(
/*МВЗ                             */@RELATIVE({МВЗ},0) and @LIST("Уч разливочных машин","СТЧ","УДЧ","№1. Аглоцех","Копровый Уч","Уч металлопродукции")
)

