         FIX(
            /*ВАЛЮТА                          */"Рубль (расчет)"
         /*Показатели                     */    
         /*ПРОЧЕЕ                         */@Relative({ПРОЧЕЕ},0) and  @Relative ("Всего по типам договоров",0)   
         /*ЭЛЕМЕНТЫ_ЗАТРАТ                    */ @Relative("Всего по усл. поставки",0), "бу элемента затрат"
         /*СЧЕТ БАЛАНС                       */"62_КЗ на начало"
         )
            "Сумма"(
            IF(@ISMBR("Февраль":"Декабрь"))
            "Сумма"=#missing;
            "Сумма"->"62_КЗ на начало" = @PRIOR("Сумма"->"62_КЗ на конец") ;
            ENDIF
            )
         ENDFIX

/*считаем курсовые разницы*/

         FIX(
            /*ВАЛЮТА                          */"USD","EUR"
         /*Показатели                     */    
         /*ПРОЧЕЕ                         */@Relative({ПРОЧЕЕ},0) and  @Relative ("Всего по типам договоров",0)   
         /*ЭЛЕМЕНТЫ_ЗАТРАТ                    */ @Relative("Всего по усл. поставки",0), "бу элемента затрат"
         /*СЧЕТ БАЛАНС                       */
         )

           FIX(
           /*Показатели                     */    "Сумма"
            )
            DATACOPY "62_КЗ на начало" to "Средний курс";
            DATACOPY "62_ДЗ на начало" to "Средний курс";

         	DATACOPY "62_КЗ на начало" to "Курсовая разница";
            DATACOPY "62_ДЗ на начало" to "Курсовая разница";            

           ENDFIX

            FIX(
            /*СЧЕТ БАЛАНС                       */"Средний курс"    
            )
           "Сумма"(
           ("62_ДЗ на начало"->"Рубль (расчет)"+"62_КЗ на начало"->"Рубль (расчет)")
           /("62_ДЗ на начало"+"62_КЗ на начало"); /*предпологаем что продажи могут быть только в одной валюте*/
           ) 
         ENDFIX

          FIX(
            /*СЧЕТ БАЛАНС                       */"Курсовая разница"    
            )
           "Сумма"(
           IF("62_ДЗ на начало">0 )
             IF("62_ДЗ на начало">="62_Постоплата")
            "62_Постоплата"->"Рубль (расчет)"-"62_Постоплата"*"Средний курс" ;
             ELSE
            "62_ДЗ на начало"*"бу видов затрат"->"бу МВЗ"->"бу контрагента"->"бу направления"->"бу вида продукции"->"бу прочих справочников"->"бу ЦФО"->"бу элемента затрат"->"бу статьи"->"бу счета баланса"->"бу МВЗ (пол-ль)"->"Курс за период"
            -"62_ДЗ на начало"->"Рубль (расчет)";
            ENDIF
          ELSE
          	IF("62_КЗ на начало">="62_Реализация с НДС"->"Всего по усл. поставки")

            "62_Реализация с НДС"->"Всего по усл. поставки"*"Средний курс"
            -"62_Реализация с НДС"->"Всего по усл. поставки"->"Рубль (расчет)";

            ELSE
            "62_КЗ на начало"->"Рубль (расчет)"
            -"62_КЗ на начало"*"бу видов затрат"->"бу МВЗ"->"бу контрагента"->"бу направления"->"бу вида продукции"->"бу прочих справочников"->"бу ЦФО"->"бу элемента затрат"->"бу статьи"->"бу счета баланса"->"бу МВЗ (пол-ль)"->"Курс за период";
            ENDIF
           ENDIF 
          )
         ENDFIX

ENDFIX  

ENDFIX
