FIX(
/*МВЗ  									*/@LIST("ТЧМ в целом", "ТЧМС в целом") and @RELATIVE({МВЗ},0)
)
FIX(
/*ПРОЧЕЕ 								*/"бу прочих справочников"
/*ПРОДУКЦИЯ  							*/"бу вида продукции"
/*НАПРАВЛЕНИЕ  							*/"бу направления"
/*ЭЛЕМЕНТЫ_ЗАТРАТ  						*/"бу элемента затрат"
/*МВЗ ПОЛУЧАТЕЛЬ  						*/"бу МВЗ (пол-ль)"
/*ВАЛЮТА  								*/"RUR"
/*ПЕРИОД  								*/@RELATIVE("Итого год",0)
/*КОНТРАГЕНТЫ  							*/"Прочие внешние","КНИ","ООО Центр Известняк"
/*ВЕРСИЯ	  							*/{ВЕРСИЯ}
/*ГОД  									*/{ГОД}
/*МВЗ  									*/
/*СЦЕНАРИЙ  							*/{СЦЕНАРИЙ}
/*ВИД ЗАТРАТ 							*/"Кварцит","Глина для глиномялки","Аглоруда Яковлевская","Долом фр. 0-40","Долом фр. 40-60(70)","Известь со стороны"
/*ЦФО  									*/"Транспортный отд"
/*СТАТЬИ  								*/"Транспортные услуги"
/*ПОКАЗАТЕЛИ  							*/
/*СЧЕТА БАЛАНСА  						*/
)

SET CREATENONMISSINGBLK ON;

"Кол-во тонн"(
IF((@ISMBR("Аглоруда Яковлевская") AND @ISMBR("Прочие внешние")) OR
  ((@ISMBR("Долом фр. 0-40") OR @ISMBR("Долом фр. 40-60(70)"))  AND @ISMBR("КНИ")) OR
  (@ISMBR("Известь со стороны") AND @ISMBR("ООО Центр Известняк") ))
"Кол-во"="Кол-во"-> "ОСиТ"->"Сырье."->"10_Закупка";
ENDIF
)
SET CREATENONMISSINGBLK OFF;

DATACOPY "Вместимость транспорта" to "Операторы";

"Операторы"(
IF( @ISMBR("Кол-во") AND  "Кол-во тонн"->"Кол-во"<>#missing   AND "Вместимость транспорта"->"Кол-во" <>#missing )
"Операторы" = @ROUND("Кол-во тонн" / "Вместимость транспорта",0);
ELSE 
"Операторы"=#missing;
ENDIF
)



DATACOPY "Операторы"->"Кол-во" to "60_Начисление без НДС"->"Сумма";
DATACOPY "Операторы"->"Кол-во" to "60_Начисление с НДС"->"Сумма";
DATACOPY "Операторы"->"Кол-во" to "60_ДЗ/КЗ на конец (+/-)"->"Сумма";

DATACOPY "60_ДЗ/КЗ на начало (+/-)"->"Сумма" to "60_ДЗ/КЗ на конец (+/-)"->"Сумма";
DATACOPY "60_Оплата"->"Сумма" to "60_ДЗ/КЗ на конец (+/-)"->"Сумма";

FIX( 
/*ПОКАЗАТЕЛИ  							*/"Сумма"
)



"60_Начисление без НДС"(
"60_Начисление без НДС"="Операторы"->"Кол-во"*"Тариф за груж вагон +7 %"->"Цена" +"Операторы"->"Кол-во"*"Ставка оператора"->"Цена" ;  
)



"60_Начисление с НДС" (
"60_Начисление с НДС" =  "60_Начисление без НДС" *((100+"Ставка НДС"->"Кол-во")/100);
)



"60_ДЗ/КЗ на конец (+/-)"(
"60_ДЗ/КЗ на конец (+/-)" = "60_ДЗ/КЗ на начало (+/-)" - "60_Начисление с НДС"+ "60_Оплата";
)




ENDFIX
ENDFIX
ENDFIX
