"Кол-во"(
IF ( (@ISMBR("Владивосток (ЖД)") OR @ISMBR("Прочие (СНГ) (ЖД)")))
   IF ( @ISMBR("ТЧМ в целом"))
      IF(@ISMBR("Чугун в целом"))
      "Чугун"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Контрагенты ТЧМ и ТЧМС"->"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"->"Отд ВЭС"->"ЭЛЕМЕНТЫ ЗАТРАТ ТЧМ И ТЧМС"->"Реализация осн продукции на экспорт"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода"
        +"Чугун"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Контрагенты ТЧМ и ТЧМС"->"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"->"Отд ВЭС"->"ЭЛЕМЕНТЫ ЗАТРАТ ТЧМ И ТЧМС"->"Реализация осн продукции в СНГ"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода";
      ENDIF
    ELSEIF ( @ISMBR("ТЧМС в целом"))/*Добавила условие Чехонина А. запрос 8382*/
       IF(@ISMBR("Прокат в целом"))
      "Прокат"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Контрагенты ТЧМ и ТЧМС"->"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"->"Отд ВЭС"->"ЭЛЕМЕНТЫ ЗАТРАТ ТЧМ И ТЧМС"->"Реализация осн продукции на экспорт"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода"
        +"Прокат"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Контрагенты ТЧМ и ТЧМС"->"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"->"Отд ВЭС"->"ЭЛЕМЕНТЫ ЗАТРАТ ТЧМ И ТЧМС"->"Реализация осн продукции в СНГ"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода";
   ENDIF
    ENDIF              
ELSEIF ((@ISDESC("ЖД отгрузка") AND NOT @ISMBR("Владивосток (ЖД)") AND NOT @ISMBR("Прочие (СНГ) (ЖД)") ) )
   IF ( @ISMBR("ТЧМ в целом"))
      IF(@ISMBR("Чугун в целом"))
      /*"Чугун"->"Всего по складам"->"Прочие внешние"->"Договор поставки"->"Отд ВЭС"->"Всего по усл. поставки"->"Реализация осн продукции на экспорт"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода"*/
      "Чугун"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Контрагенты ТЧМ и ТЧМС"->"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"->"Отд ВЭС"->"ЭЛЕМЕНТЫ ЗАТРАТ ТЧМ И ТЧМС"->"Реализация осн продукции на экспорт"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода";

