ELSEIF ( @ISMBR("Внутр рынок (ЖД)")  ) 
   IF ( @ISMBR("ТЧМ в целом"))
      IF(@ISMBR("Чугун в целом"))
       /*@SUMRANGE("Кол-во"->"Чугун"->"Всего по складам"->"Договор поставки"->"Упр по реал-ции прод-ции"->"Реализация осн продукции на внутр рынок"->"62_Реализация без НДС",@RELATIVE("Итого по контрагентам",0));*/
