)
SET CREATENONMISSINGBLK ON;

"Кол-во"(
IF ( (@ISMBR("Владивосток (Авто)") OR @ISMBR("Прочие (СНГ) (Авто)")))
   IF( @ISMBR("ТЧМС в целом"))
      IF(@ISMBR("Прокат в целом"))
/*      "Прокат"->"Всего по складам"->"Прочие внешние"->"Всего по типам договоров"->"Отд ВЭС"->"Всего по усл. поставки"->"Реализация осн продукции на экспорт"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода"
       +"Прокат"->"Всего по складам"->"Прочие внешние"->"Всего по типам договоров"->"Отд ВЭС"->"Всего по усл. поставки"->"Реализация осн продукции в СНГ"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода";*/
        "Прокат"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Контрагенты ТЧМ и ТЧМС"->"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"->"Отд ВЭС"->"ЭЛЕМЕНТЫ ЗАТРАТ ТЧМ И ТЧМС"->"Реализация осн продукции на экспорт"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода"
        +"Прокат"->"ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Контрагенты ТЧМ и ТЧМС"->"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"->"Отд ВЭС"->"ЭЛЕМЕНТЫ ЗАТРАТ ТЧМ И ТЧМС"->"Реализация осн продукции в СНГ"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода";

      ENDIF
    ENDIF

ELSEIF ((@ISDESC("Автоотгрузка") AND NOT @ISMBR("Владивосток (Авто)") AND NOT @ISMBR("Прочие (СНГ) (Авто)") ) )
   IF( @ISMBR("ТЧМС в целом"))
       IF(@ISMBR("Прокат в целом"))
      /*"Прокат"->"Всего по складам"->"Прочие внешние"->"Всего по типам договоров"->"Отд ВЭС"->"Всего по усл. поставки"->"Реализация осн продукции на экспорт"->"62_Реализация без НДС"->"Кол-во"->"Валюты ввода";*/
