"Операторы" = @ROUND("Кол-во тонн"->"Ж/Д тариф РЖД" / "Вместимость транспорта",0);




ENDFIX
FIX(
/*СТАТЬЯ                        */"Прочие выплаты"
/*НАПРАВЛЕНИЕ                       */@RELATIVE("Внутр рынок (ЖД)", 0), @RELATIVE("ЖД отгрузка", 0), "бу направления"
                               @Relative("Внутр рынок (Авто)", 0), @Relative("Автоотгрузка", 0)
/*ВИД ЗАТРАТ                      */@Relative("Всего по видам затрат",0)
/*ПОКАЗАТЕЛИ                       */
)

DATACOPY "60_Начисление без НДС"->"Сумма" TO "60_Начисление с НДС"->"Сумма";
DATACOPY "Операторы"->"Кол-во" TO "60_Начисление с НДС"->"Сумма";
DATACOPY "60_Начисление без НДС"->"Сумма" TO "60_ДЗ/КЗ на конец (+/-)"->"Сумма";
DATACOPY "Операторы"->"Кол-во" TO "60_ДЗ/КЗ на конец (+/-)"->"Сумма";
DATACOPY "60_Оплата"->"Сумма" TO "60_ДЗ/КЗ на конец (+/-)"->"Сумма";
DATACOPY "60_ДЗ/КЗ на начало (+/-)"->"Сумма" TO "60_ДЗ/КЗ на конец (+/-)"->"Сумма";
FIX(
/*ПОКАЗАТЕЛИ                       */
/*СЧЕТА БАЛАНСА                    */"60_Начисление без НДС"

)

"Сумма"(
IF(@ISMBR("Ж/Д тариф РЖД"))
   IF( "Операторы"->"Кол-во"<>#missing   AND "Тариф за груж вагон +7 %"->"Цена" <>#missing  )
    "Операторы"->"Кол-во"*"Тариф за груж вагон +7 %"->"Цена" ;
   ELSE 
   #missing;
    ENDIF
ELSEIF(@ISMBR("Ж/Д тариф прочих перевозчиков.")  or @ISMBR("Доставка продукции автотранспортом") ) 
   IF( @ISMBR("Сумма") AND  "Операторы"->"Кол-во"<>#missing   AND "Ставка оператора"->"Цена" <>#missing    )
    "Операторы"->"Кол-во"*"Ставка оператора"->"Цена" ;
   ELSE 
   #missing;
   ENDIF
ELSEIF(@ISMBR("Теплушки") OR @ISMBR("Доставка прод. до ст.отправления") OR @ISMBR("Плата за польз. вагонами ОАО РЖД и СНГ")) 
   "Операторы"->"Кол-во"*"Тариф за груж вагон +7 %"->"Цена" +"Операторы"->"Кол-во"*"Ставка оператора"->"Цена" ;

ENDIF
)
ENDFIX

FIX(
/*ПОКАЗАТЕЛИ                       */
/*СЧЕТА БАЛАНСА                    */"60_Начисление с НДС"
)

"Сумма" =  "Сумма"->"60_Начисление без НДС" *((100+"Ставка НДС"->"Кол-во")/100);

ENDFIX

FIX(
/*ПОКАЗАТЕЛИ                       */
/*СЧЕТА БАЛАНСА                    */"60_ДЗ/КЗ на конец (+/-)"
)
"Сумма"(
   IF(@ISMBR("Январь"))
   "Сумма"->"60_ДЗ/КЗ на начало (+/-)"- "Сумма"->"60_Начисление с НДС"+ "Сумма"->"60_Оплата";
    ELSEIF(@ISMBR("Февраль":"Декабрь"))
    @PRIOR("Сумма"->"60_ДЗ/КЗ на конец (+/-)" ) - "Сумма"->"60_Начисление с НДС"+ "Сумма"->"60_Оплата";
     ENDIF
)

ENDFIX


ENDFIX    

