    "60_Начисление без НДС"=@CREATEBLOCK("Тариф за груж вагон +7 %");

	 "Операторы"->"Кол-во"*"Тариф за груж вагон +7 %"->"Цена" ;
	ELSE 
	#missing;
    ENDIF
ELSEIF(@ISMBR("Ж/Д тариф прочих перевозчиков.")  or @ISMBR("Доставка продукции автотранспортом") ) 
	IF( @ISMBR("Сумма") AND  "Операторы"->"Кол-во"<>#missing   AND "Ставка оператора"->"Цена" <>#missing    )

    "60_Начисление без НДС"=@CREATEBLOCK("Операторы");
    "60_Начисление без НДС"=@CREATEBLOCK("Ставка оператора");

	 "Операторы"->"Кол-во"*"Ставка оператора"->"Цена" ;
	ELSE 
	#missing;
	ENDIF
ELSEIF(@ISMBR("Теплушки") OR @ISMBR("Доставка прод. до ст.отправления") OR @ISMBR("Плата за польз. вагонами ОАО РЖД и СНГ"))

    "60_Начисление без НДС"=@CREATEBLOCK("Операторы");
    "60_Начисление без НДС"=@CREATEBLOCK("Тариф за груж вагон +7 %");
    "60_Начисление без НДС"=@CREATEBLOCK("Ставка оператора");

	"Операторы"->"Кол-во"*"Тариф за груж вагон +7 %"->"Цена" +"Операторы"->"Кол-во"*"Ставка оператора"->"Цена" ;

ENDIF
)

"60_Начисление с НДС"(
"60_Начисление с НДС"=@CREATEBLOCK("60_Начисление без НДС");
"60_Начисление с НДС"=@CREATEBLOCK("Ставка НДС");

"60_Начисление с НДС" =  "60_Начисление без НДС" *(100+"Ставка НДС"->"Кол-во")/100;
)


"60_ДЗ/КЗ на конец (+/-)" (
	IF(@ISMBR("Январь"))

    "60_ДЗ/КЗ на конец (+/-)"=@CREATEBLOCK("60_ДЗ/КЗ на начало (+/-)");
    "60_ДЗ/КЗ на конец (+/-)"=@CREATEBLOCK("60_Начисление с НДС");
    "60_ДЗ/КЗ на конец (+/-)"=@CREATEBLOCK("60_Оплата");

	"60_ДЗ/КЗ на начало (+/-)"- "60_Начисление с НДС"+ "60_Оплата";
    ELSEIF(@ISMBR("Февраль":"Декабрь"))

    "60_ДЗ/КЗ на конец (+/-)"=@CREATEBLOCK("60_ДЗ/КЗ на начало (+/-)");
    "60_ДЗ/КЗ на конец (+/-)"=@CREATEBLOCK("60_Начисление с НДС");
    "60_ДЗ/КЗ на конец (+/-)"=@CREATEBLOCK("60_Оплата");

    @PRIOR("60_ДЗ/КЗ на конец (+/-)" ) - "60_Начисление с НДС"+ "60_Оплата";
  	ENDIF
)



/*SET CREATENONMISSINGBLK OFF;
	CLEARBLOCK EMPTY;*/


ENDFIX

