	+"66_Начисление %"->"Займы полученные."<>#missing)


"60_Начисление без НДС" = "66_Начисление %"->"Кредиты полученные"
						+ "66_Начисление %"->"Займы полученные.";

"60_Начисление с НДС"= "66_Начисление %"->"Кредиты полученные"
						+ "66_Начисление %"->"Займы полученные.";					
else

"60_Начисление без НДС"=#missing;
"60_Начисление с НДС"=#missing;
endif
ELSE
