"60_ДЗ/КЗ на конец (+/-)"(
IF (@ISMBR(@relative("Всего год",0)))
IF(@PRIOR("60_ДЗ/КЗ на конец (+/-)",1,@RELATIVE("Период",0))-"60_Начисление с НДС"+"60_Оплата аванса"+"60_Окончат расчет"<>#missing)
"60_ДЗ/КЗ на конец (+/-)"=@PRIOR("60_ДЗ/КЗ на конец (+/-)",1,@RELATIVE("Период",0))-"60_Начисление с НДС"+"60_Оплата аванса"+"60_Окончат расчет";
ELSE
"60_ДЗ/КЗ на конец (+/-)"=#missing;
ENDIF
ENDIF
)
ENDFIX

