			"Сумма"->"ТЧМ в целом"->"Списание_себестоимости"->"бу направления"->"Коммерческие расходы."->"бу контрагента"->"бу МВЗ (пол-ль)"->"бу прочих справочников"->"бу видов затрат" =
			"Сумма"->"ТЧМ в целом"->"Списание_себестоимости"->"бу направления"->"Коммерческие расходы."->"бу контрагента"->"бу МВЗ (пол-ль)"->"бу прочих справочников"->"бу видов затрат"
			+"Сумма";
		ELSE
			"Сумма"->"ТЧМС в целом"->"Списание_себестоимости"->"бу направления"->"Коммерческие расходы."->"бу контрагента"->"бу МВЗ (пол-ль)"->"бу прочих справочников"->"бу видов затрат" =
			"Сумма"->"ТЧМС в целом"->"Списание_себестоимости"->"бу направления"->"Коммерческие расходы."->"бу контрагента"->"бу МВЗ (пол-ль)"->"бу прочих справочников"->"бу видов затрат"
			+"Сумма";
		ENDIF;)

        "Кол-во"(
		IF(@ISDESC("ТЧМ итого"))
			"Кол-во"->"ТЧМ в целом"->"Списание_себестоимости"->"бу направления"->"Коммерческие расходы."->"бу контрагента"->"бу МВЗ (пол-ль)"->"бу прочих справочников"->"бу видов затрат" =
			"Кол-во"->"ТЧМ в целом"->"Списание_себестоимости"->"бу направления"->"Коммерческие расходы."->"бу контрагента"->"бу МВЗ (пол-ль)"->"бу прочих справочников"->"бу видов затрат"
			+"Кол-во";
		ELSE
			"Кол-во"->"ТЧМС в целом"->"Списание_себестоимости"->"бу направления"->"Коммерческие расходы."->"бу контрагента"->"бу МВЗ (пол-ль)"->"бу прочих справочников"->"бу видов затрат" =
			"Кол-во"->"ТЧМС в целом"->"Списание_себестоимости"->"бу направления"->"Коммерческие расходы."->"бу контрагента"->"бу МВЗ (пол-ль)"->"бу прочих справочников"->"бу видов затрат"
			+"Кол-во";
		ENDIF;)


ENDFIX

