FIX(
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */
/*ЦФО                            */"ПЭО"
/*КОНТРАГЕНТЫ                    */"бу контрагента"
/*НАПРАВЛЕНИЕ                    */"бу направления"
/*ПРОЧЕЕ                          */
/*СЧЕТА БАЛАНСА                   */
/*МВЗ ПОЛУЧАТЕЛЬ                  */
/*ВАЛЮТА                          */"RUR"
/*ВИД ЗАТРАТ                      */"Общехозяйственные расходы"
/*СТАТЬИ                          */"УПРАВЛЕНЧЕСКИЕ РАСХОДЫ"
/*СЦЕНАРИЙ                      */{СЦЕНАРИЙ}
/*ПЕРИОД                       */@ILDESCENDANTS({СПИСОК_ПЕРИОД}) and @RELATIVE("Всего год",0)
/*ВЕРСИЯ                      */{ВЕРСИЯ}
/*ГОД                         */{ГОД}
/*ПРОДУКЦИЯ                       */
/*ПОКАЗАТЕЛИ                       */
/*МВЗ                             */
)

    FIXPARALLEL(8,@Descendants("Всего по первичным элементам затрат."))


        FIX(
         /*ПОКАЗАТЕЛИ                       */"Сумма","Кол-во"
        /*МВЗ ПОЛУЧАТЕЛЬ                  */"бу МВЗ (пол-ль)",@RELATIVE(@MEMBER(@CONCATENATE(@NAME({МВЗ})," (пол-ль)")),0)
         /*СЧЕТА БАЛАНСА                   */"20_Начисление"
        /*ПРОДУКЦИЯ                       */@Relative("Всего по продукции", 0)
        /*ПРОЧЕЕ                          */ 
        )
               AGG("ПРОЧЕЕ");


        ENDFIX

        FIX(
         /*ПОКАЗАТЕЛИ                       */"Сумма","Кол-во"
        /*МВЗ ПОЛУЧАТЕЛЬ                  */
         /*СЧЕТА БАЛАНСА                   */"20_Начисление"
        /*ПРОДУКЦИЯ                       */"ПРОДУКЦИЯ ТЧМ и ТЧМС"
        /*ПРОЧЕЕ                          */@Relative("Всего по продукции", 0)
        )
               AGG("МВЗ ПОЛУЧАТЕЛЬ");


        ENDFIX

        FIX(
         /*ПОКАЗАТЕЛИ                       */
        /*МВЗ ПОЛУЧАТЕЛЬ                  */"МВЗ Получатели"
         /*СЧЕТА БАЛАНСА                   */"20_Начисление"
        /*ПРОДУКЦИЯ                       */@Relative("Всего по продукции", 0)
        /*ПРОЧЕЕ                          */"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"
        )

        "Сумма" (
            "Сумма"->"Прочие МВЗ" = "Сумма" + "Сумма"->"Прочие МВЗ";)

       "Кол-во" (
            "Кол-во"->"Прочие МВЗ" = "Кол-во" + "Кол-во"->"Прочие МВЗ";)

        ENDFIX

   ENDFIXPARALLEL

ENDFIX


