FIX( 
/*ПОКАЗАТЕЛИ                      */"Сумма"
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */"Технически элемент" 
/*СТАТЬИ                         */"бу статьи"
)   
 FIXPARALLEL(8,@Descendants("Всего по продукции"))
"ЦЕХОВАЯ СЕБЕСТОИМОСТЬ ТП" = @ALLOCATE("ЦЕХОВАЯ СЕБЕСТОИМОСТЬ ТП"->"Технически элемент",@DESCENDANTS("Всего по первичным элементам затрат."),"20_Начисление"-> "ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"->"МВЗ Получатели"->"Прочие МВЗ"->"Общехозяйственные расходы"->"УПРАВЛЕНЧЕСКИЕ РАСХОДЫ",,share);
ENDFIXPARALLEL
ENDFIX

FIX(
/*СЧЕТА БАЛАНСА                   */"ЦЕХОВАЯ СЕБЕСТОИМОСТЬ ТП"
/*ПОКАЗАТЕЛИ                      */
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */@REMOVE(@RELATIVE("Всего по первичным элементам затрат.",0),"МПЗ собственного производства в целом (первич.)")
/*СТАТЬИ                         */"бу статьи"
)   
 FIXPARALLEL(8,@Descendants("Всего по продукции"))
 "Сумма"(
  IF("Статья ФП ЭЗ"< 33 and "Статья ФП ЭЗ" >0 )
