/*ПРОЧЕЕ                          */
/*СЧЕТА БАЛАНСА                   */
/*МВЗ ПОЛУЧАТЕЛЬ                  */
/*ВАЛЮТА                          */"RUR"
/*ВИД ЗАТРАТ                      */"Общехозяйственные расходы"
/*СТАТЬИ                          */"УПРАВЛЕНЧЕСКИЕ РАСХОДЫ"
/*СЦЕНАРИЙ                        */{СЦЕНАРИЙ}
/*ПЕРИОД                          */@RELATIVE("Всего год",0)
/*ВЕРСИЯ                          */{ВЕРСИЯ}
/*ГОД                             */{ГОД}
/*ПРОДУКЦИЯ                       */
/*ПОКАЗАТЕЛИ                       */"Сумма","Кол-во"
/*МВЗ                             */
)

    FIXPARALLEL(8,@Descendants("Всего по первичным элементам затрат."))

        FIX(
        /*МВЗ ПОЛУЧАТЕЛЬ                  */"МВЗ получатели","бу МВЗ (пол-ль)",@RELATIVE(@MEMBER(@CONCATENATE(@NAME({МВЗ})," (пол-ль)")),0)
      /*ПРОДУКЦИЯ                       */"ПРОДУКЦИЯ ТЧМ и ТЧМС"
      /*СЧЕТА БАЛАНСА                   */"20_Начисление"
        /*ПРОЧЕЕ                          */ @Relative("Всего по прочим справочникам в смете", 0),"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"
        "Прочие МВЗ"
        )
               CLEARBLOCK ALL;
        ENDFIX



   ENDFIXPARALLEL
 ENDFIX 

