rule.script:
SET AGGMISSG ON;
SET UPDATECALC OFF;
SET CREATEBLOCKONEQ OFF;
SET CREATENONMISSINGBLK OFF;
SET FRMLBOTTOMUP OFF;
SET REMOTECALC OFF;
SET CALCPARALLEL 1;
SET CALCTASKDIMS 1;
SET EMPTYMEMBERSETS ON;
SET FRMLRTDYNAMIC OFF;
SET MSG INFO;
/* Проверка корректности рассчитываемого среза данных */

FIX(
/* ВАЛЮТА							*/	"бу валюты"
/* ПЕРИОД							*/	"P0"
/* ГОД								*/	"No Year"
/* ВЕРСИЯ							*/	
/* СЦЕНАРИЙ							*/	
/* ЦФО								*/	"бу ЦФО"
/* МВЗ								*/	"бу МВЗ"
/* МВЗ ПОЛУЧАТЕЛЬ					*/	"бу МВЗ (пол-ль)"
/* СЧЕТА БАЛАНСА					*/	"80_Увеличение"
/* СТАТЬИ							*/	
/* ПОКАЗАТЕЛИ						*/	"Сумма"
/* ВИД ЗАТРАТ						*/	"бу видов затрат"
/* ПРОДУКЦИЯ						*/	"бу вида продукции"
/* КОНТРАГЕНТЫ						*/	"бу контрагента"
/* НАПРАВЛЕНИЕ	*/	"бу направления"
/* ЭЛЕМЕНТЫ_ЗАТРАТ					*/	"бу элемента затрат"
/* ПРОЧЕЕ							*/	"бу прочих справочников"

)
  SET CREATENONMISSINGBLK ON;


  var ErrFlagScenario=0;
  var ErrFlagVersion=0;
  var NextM=2;
  /* ПРОВЕРКА КОРРЕКТНОСТИ ВЫБОРА СЦЕНАРИЯ */
  FIX(/* Сценарий */ {СЦЕНАРИЙ} AND @LIST(&ОТКРЫТЫЕ_СЦЕНАРИИ))
    FIX(
    /* Сценарий */ 
    /* Версия   */ "Рабочая 1"
    )
     "бу статьи" (
     IF (ErrFlagScenario==0)
            ErrFlagScenario=1;
     ENDIF
     );

    ENDFIX
  ENDFIX

   /* ПРОВЕРКА КОРРЕКТНОСТИ ВЫБОРА ВЕРСИИ */
  FIX(/* Версия  */ {ВЕРСИЯ} AND @LIST(&ОТКРЫТЫЕ_ВЕРСИИ))
    FIX(
    /* Сценарий */ "Проект"
    /* Версия   */ 
    )
     "бу статьи" (
     IF (ErrFlagVersion==0)
            ErrFlagVersion=1;
     ENDIF
     );
    ENDFIX
  ENDFIX

  /* ВЫВОД СООБЩЕНИЙ О НЕКОРРЕКТНОМ СРЕЗЕ */
  FIX(
  /* Сценарий     */ "Проект"
  /* Версия       */ "Рабочая 1"
  )

   "бу статьи" (
    IF (ErrFlagScenario==0)
       @RETURN("Выбран сценарий, закрытый для расчета. Расчет не произведен.",ERROR);
    ENDIF
    IF (ErrFlagVersion==0)
       @RETURN("Выбрана версия, закрытая для расчета. Расчет не произведен.",ERROR);
    ENDIF
    )

   ENDFIX
ENDFIX
SET CREATENONMISSINGBLK OFF;
/* Распределение жд тарифа к сырью доменного цеха */

FIX(
/*ЭЛЕМЕНТЫ ЗАТРАТ					 */"бу элемента затрат"
/*ЦФО 						  		 */"ПЭО"
/*КОНТРАГЕНТЫ 						 */"бу контрагента"
/*НАПРАВЛЕНИЕ 	 			         */"бу направления"
/*ПРОЧЕЕ 							 */"бу прочих справочников"
/*СЧЕТА БАЛАНСА                      */"20_Начисление"
/*МВЗ ПОЛУЧАТЕЛЬ 					 */"бу МВЗ (пол-ль)"
/*ВАЛЮТА 							 */"RUR"
/*ВИД ЗАТРАТ 						 */
/*СТАТЬИ							 */
/*СЦЕНАРИЙ							 */{СЦЕНАРИЙ}
/*ПЕРИОД							 */@RELATIVE("Итого год",0)
/*ВЕРСИЯ 							 */{ВЕРСИЯ}
/*ГОД 								 */{ГОД}
/*ПРОДУКЦИЯ 						 */
/*ПОКАЗАТЕЛИ 						 */
/*МВЗ 								 */
)

	FIX(
	/*МВЗ ПОЛУЧАТЕЛЬ 					 */"бу МВЗ (пол-ль)"
	)

        /* Сохраняем жд тариф по грузоперевозкам */
        FIX(
        /*МВЗ 								*/"Грузоперевозки_"
        /*Статья							*/"Внутризаводское перемещение грузов."
        /*ВИД ЗАТРАТ						*/"Грузоперевозки сырья."
        /*ПОКАЗАТЕЛИ 						*/
        /*ПРОДУКЦИЯ 						*/"Агломерат бункерный"
        )
        	SET CREATENONMISSINGBLK ON;
            "Тариф жд внутр."(
            /* Проверяем есть ли сумма по "Внутризаводское перемещение грузов.", по "Грузоперевозки сырья." 
               при наличии кол-ва перевезенного сырья и топлива из формы 2.5.07 */
            IF  ("Сумма"->"№1. Аглоцех"->"№1. Аглоцех (пол-ль)"+0 <> 0
            	AND "бу видов затрат"->"Цехи п-п (сч 20;23;26)"->"Грузоперевозки сырья"->"бу статьи"->"20_Выбытие"->"№1. Аглоцех (пол-ль)"->"Кол-во с учетом годного выхода"+0 <> 0)
                "Сумма"->"№1. Аглоцех"->"№1. Аглоцех (пол-ль)"
                / "Кол-во"->"№1. Аглоцех"->"№1. Аглоцех (пол-ль)";
            ELSEIF ("Сумма"->"№1. Аглоцех"->"№1. Аглоцех (пол-ль)"+0 == 0
            	AND "бу видов затрат"->"Цехи п-п (сч 20;23;26)"->"Грузоперевозки сырья"->"бу статьи"->"20_Выбытие"->"№1. Аглоцех (пол-ль)"->"Кол-во с учетом годного выхода"+0 == 0)
            	#MISSING;
            ENDIF
            )   
            SET CREATENONMISSINGBLK OFF;
        ENDFIX

        /* Добавление жд тарифа по ВЗ Аглоцеха */
        FIX(
        /*МВЗ								*/@RELATIVE("№1. Аглоцех",0)
        /*ВИД ЗАТРАТ 						*/"Концентрат КМА", "Концентрат ЛГОК", "Концентрат МГОК", "Концентрат СГОК", 
						                  	  "Аглоруда Яковлевская", "Материалы железосодержащие АС", "Доломит.", "Известняк фр. 0-20", 
                                              "Известняк фр. 20-40", "Известь со стороны", "Отсев кокса мелкофракционный АС"
        /*Статья							*/"Сырье и основные материалы.","Топливо технологическое."
        /*ПОКАЗАТЕЛИ 						*/
        /*ПРОДУКЦИЯ 						*/"Агломерат бункерный"
        )
             SET CREATENONMISSINGBLK ON;
            "Цена"(
            IF ("Кол-во"+0 <> 0 /*AND "Цена" <> "Тариф жд внутр."*/)
                "Тариф жд внутр." = "Сумма"/"Кол-во" + "Тариф жд внутр."->"Грузоперевозки сырья."->"Внутризаводское перемещение грузов."->"Грузоперевозки_";        
                "Цена" = "Тариф жд внутр.";
                "Сумма" =  "Тариф жд внутр." * "Кол-во";
            ENDIF
            )
             SET CREATENONMISSINGBLK OFF;
        ENDFIX

        /* Очищаем ВЗ Грузоперевозки сырья., т.к. мы их перенесли на сырье и топливо */
        FIX(
        /*МВЗ 								*/@RELATIVE("№1. Аглоцех",0)
        /*Статья							*/"Внутризаводское перемещение грузов."
        /*ВИД ЗАТРАТ						*/"Грузоперевозки сырья."
        /*ПОКАЗАТЕЛИ 						*/
        /*ПРОДУКЦИЯ 						*/"Агломерат бункерный"
        /*МВЗ ПОЛУЧАТЕЛЬ 					*/"№1. Аглоцех (пол-ль)"
        )

            "Сумма" = #MISSING;
            "Кол-во" = #MISSING;
            "Цена" = #MISSING;

        ENDFIX
    ENDFIX


ENDFIX
