"10_Списание"->"Сумма" -> "бу контрагента" = "10_Списание"->"Кол-во"->"бу контрагента"
*(("Сумма"->"10_Остаток на начало"->"Итого по контрагентам"->"P0"+"Сумма"->"10_Поступление"->"Итого по контрагентам" )
/("Кол-во"->"10_Остаток на начало"->"Итого по контрагентам"->"P0"+"Кол-во"->"10_Поступление"->"Итого по контрагентам"));
ELSEIF ( @ISMBR("бу контрагента") AND NOT @ISMBR("Январь"))
/*"10_Списание"->"Сумма"-> "бу контрагента"  = "10_Списание"->"Кол-во"-> "бу контрагента" 
*((@PRIOR("Сумма"->"10_Остаток на конец"->"Итого по контрагентам")+"Сумма"->"10_Поступление"->"Итого по контрагентам" )
/(@PRIOR("Кол-во"->"10_Остаток на конец"->"Итого по контрагентам")+"Кол-во"->"10_Поступление"->"Итого по контрагентам"));
*/
"10_Списание"->"Сумма"-> "бу контрагента"  = "10_Списание"->"Кол-во"-> "бу контрагента" 
*
("10_Остаток на начало"->"Итого по контрагентам"->"Январь"->"Сумма"+@SUMRANGE("10_Поступление"->"Итого по контрагентам"->"Сумма",@LIST("Январь":@CURRMBR("ПЕРИОД"))))
/("10_Остаток на начало"->"Итого по контрагентам"->"Январь"->"Кол-во"+@SUMRANGE("10_Поступление"->"Итого по контрагентам"->"Кол-во",@LIST("Январь":@CURRMBR("ПЕРИОД"))));

ENDIF
)

ENDFIX 

