FIX(
/*СЧЕТА БАЛАНСА                    */"10_Остаток на конец"
)
"Сумма"(
"10_Остаток на конец"=#missing;
IF(@ISMBR("Январь"))
"10_Остаток на начало" +"10_Поступление"-"10_Списание";
ELSEIF( @ISMBR("Февраль":"Декабрь") )
"10_Остаток на начало"->"Январь"
								+ @SUMRANGE("10_Поступление",@LIST("Январь":@CURRMBR("ПЕРИОД")))
								- @SUMRANGE("10_Списание",@LIST("Январь":@CURRMBR("ПЕРИОД")));
ENDIF
)
ENDFIX

FIX(
/*СЧЕТА БАЛАНСА                    */"10_Остаток на начало"
)
"Сумма"(
IF(@ISMBR("Февраль":"Декабрь"))
"10_Остаток на начало"=#missing;
@PRIOR("10_Остаток на конец");
ENDIF
)

ENDFIX
ENDFIX
ENDFIX
