        "Цена"="Цена тех."->"20_Начисление"+"ССУ"->"Договорной объем";
		ELSEIF(@ISMBR("Соцобъекты."))
		"Кол-во"="Кол-во"->"Договорной объем";
        "Цена"="Цена"->"Договорной объем";
        ELSE
        "Кол-во"="Кол-во"->"Договорной объем";
		ENDIF
        )

		"Сумма"="Кол-во"*"Цена";
     ENDFIX   
    FIX(
			/*СЧЕТА БАЛАНСА                   */"60_Начисление с НДС"	
        )
