        0-"60_ДЗ/КЗ на начало (+/-)";
        ELSE
		"Сумма"->"Договорной объем"*0.85-"60_ДЗ/КЗ на начало (+/-)"->"Сумма";
		ENDIF
		)



	ENDFIX 

	FIX(
