		ELSEIF (@ISMBR("Февраль":"Декабрь"))
		@PRIOR("60_ДЗ/КЗ на конец (+/-)")-"60_Начисление с НДС"+"60_Окончат расчет";
		ENDIF 
		)
		ENDFIX 

	SET CREATENONMISSINGBLK OFF;
	CLEARBLOCK EMPTY;

