"10_Поступление"( "Цена"->"Кокс влажный металлургический"->"Кокс"->"Топливо." = "Кокс влажный металлургический"->"ТЧМ в целом"->"Кокс"->"бу прочих справочников"->"ОСиТ"->"Топливо."->"10_Поступление"->"Сумма" /"Кокс влажный металлургический"->"Кокс"->"Топливо."->"10_Поступление"->"Кол-во"; "Цена"->"Кокс влажный металлургический"->"АО Москокс"->"Топливо." = "Кокс влажный металлургический"->"ТЧМ в целом"->"АО Москокс"->"бу направления"-> "бу вида продукции"->"бу прочих справочников"->"ОСиТ"->"бу элемента затрат"->"Топливо."-> "10_Закупка"->"бу МВЗ (пол-ль)"->"Цена"->"RUR" / (1 - "Влага"->"Доменные печи"->"Чугун"->"бу прочих справочников"->"ПЭО"->"бу элемента затрат"->"Топливо технологическое."->"20_Начисление"->"Норма на ед" / 100);
IF(@ISMBR("Агломерат бункерный.")) "Цена" = "Сумма"/"Кол-во"; ELSEIF( @ISMBR("Отсев агломерата")) "Цена" = "Концентрат КМА"->"ТЧМ в целом"->"КМАруда"->"бу направления"-> "бу вида продукции"->"бу прочих справочников"->"ОСиТ"->"бу элемента затрат"->"Сырье."-> "10_Закупка"->"бу МВЗ (пол-ль)"->"Цена"->"RUR"; "Сумма" = "Кол-во" * "Цена"; ELSEIF (@ISMBR("Агломерат скиповый")) "Сумма"->"Агломерат скиповый" = "Сумма"->"Агломерат бункерный." - "Сумма"->"Отсев агломерата"; "Цена" = "Сумма"/"Кол-во"; ENDIF )
ENDFIX
