/*СТАТЬИ							  */@RELATIVE("ПОЛНАЯ СЕБЕСТОИМОСТЬ",0)
/*СЦЕНАРИЙ							  */{СЦЕНАРИЙ}
/*ПЕРИОД							  */@RELATIVE("Итого год",0)
/*ВЕРСИЯ 							  */{ВЕРСИЯ}
/*ГОД 								  */{ГОД}
/*ПРОДУКЦИЯ 						  */"Грузоперевозки."
/*ПОКАЗАТЕЛИ 						   */"Сумма"
/*МВЗ 									*/"Грузоперевозки_"
)

FIX(
/*ВИД ЗАТРАТ 						  */@RELATIVE("Всего по видам затрат",0)
/*МВЗ ПОЛУЧАТЕЛЬ 					  */	
)

AGG("МВЗ ПОЛУЧАТЕЛЬ");

ENDFIX


FIX(
/*ВИД ЗАТРАТ 						  */
/*МВЗ ПОЛУЧАТЕЛЬ 					  */"МВЗ получатели"	
)

AGG("ВИД_ЗАТРАТ");

ENDFIX
ENDFIX
FIX(
/*ПРОЧЕЕ 								*/"Прочее по ЭР"
/*ПРОДУКЦИЯ  							*/"бу вида продукции"
/*НАПРАВЛЕНИЕ  							*/"бу направления"
/*ЭЛЕМЕНТЫ_ЗАТРАТ  						*/"бу элемента затрат"
/*МВЗ ПОЛУЧАТЕЛЬ  						*/"бу МВЗ (пол-ль)"
/*ВАЛЮТА  								*/"RUR"
/*ПЕРИОД  								*/@RELATIVE("Итого год",0)
/*КОНТРАГЕНТЫ  							*/"бу контрагента"
/*ВЕРСИЯ	  							*/{ВЕРСИЯ}
/*ГОД  									*/{ГОД}
/*МВЗ  									*/"ОПР Доменный цех" 
/*СЦЕНАРИЙ  							*/{СЦЕНАРИЙ}
/*ВИД ЗАТРАТ 							*/
/*ЦФО  									*/"ПЭО"
/*СТАТЬИ  								*/"бу статьи"
/*ПОКАЗАТЕЛИ  							*/
/*СЧЕТА БАЛАНСА  						*/
)

SET CREATENONMISSINGBLK ON;

	FIX(
	/*ВИД ЗАТРАТ 							*/
	/*СЧЕТА БАЛАНСА  						*/"10_Списание"
	)
		FIX (
		/*ВИД ЗАТРАТ   */"Кокс влажный скиповой"
		)

			"Кол-во" = "Кол-во"->"Топливо."->"Кокс после рассева по бу";
			"Сумма" = "Сумма"->"Топливо."->"Кокс после рассева по бу";

		ENDFIX
        FIX (
		/*ВИД ЗАТРАТ   */"Коксовый орех +25"
		)

			"Кол-во" = #MISSING;
			"Сумма" = #MISSING;

		ENDFIX
        FIX(
        /*ВИД ЗАТРАТ   */"Коксовый орех"
		)

			"Кол-во" = "Кол-во"->"Топливо."->"Коксовый орех +25";
			"Сумма" = "Сумма"->"Топливо."->"Коксовый орех +25";

		ENDFIX
		FIX(
		/*ВИД ЗАТРАТ   */"Коксовая мелочь с печей","Коксовая мелочь с РВО","Коксовый орех с РВО"
		)
			"Кол-во" = "Кол-во"->"Топливо."->"10_Поступление";
			"Сумма" = "Сумма"->"Топливо."->"10_Поступление";
		ENDFIX
		FIX(
		/*ВИД ЗАТРАТ   */"Агломерат скиповый", "Отсев агломерата"
		)
			"Кол-во" = "Кол-во"->"Сырье."
/*!!! удалить как только в РМЦ появится собственное движение*/
			-"Литейный Уч"->"бу контрагента"->"бу направления"->"Жидкая сталь на литье."->"бу прочих справочников"->"ПЭО"->"бу элемента затрат"->"Доб.материалы и флюсы"->"20_Начисление"->"бу МВЗ (пол-ль)"->"Кол-во"->"RUR";
			"Сумма" = "Сумма"->"Сырье."
/*!!! удалить как только в РМЦ появится собственное движение*/            
            -("Агломерат скиповый"->"ОПР Доменный цех"->"бу контрагента"->"бу направления"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"бу элемента затрат"->"Сырье."->"10_Списание"->"бу МВЗ (пол-ль)"->"Кол-во"->"RUR"
            -"Агломерат скиповый"->"ОПР Доменный цех"->"бу контрагента"->"бу направления"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"бу элемента затрат"->"Сырье."->"10_Поступление"->"бу МВЗ (пол-ль)"->"Кол-во"->"RUR")
            *"Агломерат скиповый"->"ОПР Доменный цех"->"бу контрагента"->"бу направления"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"бу элемента затрат"->"Сырье."->"10_Списание"->"бу МВЗ (пол-ль)"->"Цена."->"RUR"
            ;
		ENDFIX
	ENDFIX

	/* Расчет на бу счета баланса по алгоритмам АФ */
	FIX(
	/*ВИД ЗАТРАТ 							*/"Кокс влажный скиповой","Коксовый орех"
	/*СЧЕТА БАЛАНСА  						*/"бу счета баланса"
	)

		"Цена" 
        /* первая дробь это цеховая себестоимость "Сумма на единицу" из калькуляции ЖДЦ по продукции "Грузоперевозки" (форма 2.5.10.ЖДЦ_Кальк)*/
			= "Сумма"-> "ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Грузоперевозки_"->"Грузоперевозки."->"бу прочих справочников"->"бу элемента затрат"->"ПОЛНАЯ СЕБЕСТОИМОСТЬ"->"20_Начисление"->"МВЗ ПОЛУЧАТЕЛИ"
            / "Кол-во"->"бу видов затрат"->"Грузоперевозки_"->"Грузоперевозки."->"бу прочих справочников"->"Кол-во с учетом годного выхода."->"бу элемента затрат"->"20_Начисление"->"бу МВЗ (пол-ль)"
			+ "Сумма"->"Топливо технологическое"->"10_Списание"
			/ "Кол-во"->"Топливо технологическое"->"10_Списание";

		"Сумма" = "Цена" * "Кол-во"->"10_Списание";
	ENDFIX

	FIX(
	/*ВИД ЗАТРАТ 							*/"Коксовая мелочь с печей","Коксовая мелочь с РВО","Коксовый орех с РВО"
	/*СЧЕТА БАЛАНСА  						*/"бу счета баланса"
	)

		"Цена" 
        	= "Сумма"->"Топливо технологическое"->"10_Списание"
			/ "Кол-во"->"Топливо технологическое"->"10_Списание"
            - "Топливо."->"10_Поступление"->"Цена";

		"Сумма" = "Цена" * "Кол-во"->"10_Списание";

	ENDFIX

	FIX(
	/*ВИД ЗАТРАТ 							*/"Агломерат скиповый", "Отсев агломерата"
	/*СЧЕТА БАЛАНСА  						*/"бу счета баланса"
	)

		"Цена"(
			"Отсев агломерата"->"Цена"
				= "Сумма"->"Агломерат скиповый"->"10_Списание" 
				/ ("Кол-во"->"Агломерат скиповый"->"10_Списание"
				+ "Кол-во"->"Отсев агломерата"->"10_Списание") 
				- "Цена"->"Отсев агломерата"->"ОПР Доменный цех"->"10_Поступление"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"Сырье.";

			"Отсев агломерата"->"Сумма" = "Отсев агломерата"->"Кол-во"->"10_Списание" * "Отсев агломерата"->"Цена";

			"Агломерат скиповый"->"Цена" 
				= ("Сумма"->"Агломерат скиповый"->"10_Списание" 
				- "Отсев агломерата"->"Сумма")
				/ "Кол-во"->"Агломерат скиповый"->"10_Списание";

			"Агломерат скиповый"->"Сумма" = "Агломерат скиповый"->"Цена" * "Агломерат скиповый"->"Кол-во"->"10_Списание";
		)
	ENDFIX

	FIX(
	/*ВИД ЗАТРАТ 							*/"Кокс влажный скиповой","Коксовый орех","Коксовая мелочь с печей","Коксовая мелочь с РВО",
											  "Коксовый орех с РВО","Агломерат скиповый", "Отсев агломерата"
	/*СЧЕТА БАЛАНСА  						*/"бу счета баланса"
	)

	"Кол-во" = "Сумма"/"Цена";

	ENDFIX

SET CREATENONMISSINGBLK OFF;
CLEARBLOCK EMPTY;

ENDFIX
/*зачищаем данные */

FIX(
/*ЭЛЕМЕНТЫ_ЗАТРАТ					 */"бу элемента затрат"
/*ЦФО 						  		 */"ПЭО"
/*КОНТРАГЕНТЫ 						 */"бу контрагента"
