/* Перенос цен  из формы 6.15.1.Кокс в калькуляции ДЦ */
FIX(
/*НАПРАВЛЕНИЕ 								*/"бу направления"
/*ПРОДУКЦИЯ 								*/@RELATIVE("Чугун",0)
/*ПРОЧЕЕ 									*/"бу прочих справочников"
/*ЭЛЕМЕНТЫ_ЗАТРАТ 							*/"бу элемента затрат"
/*ЦФО 										*/"ПЭО"
/*ВАЛЮТА 									*/"RUR"
/*КОНТРАГЕНТЫ 								*/"бу контрагента"
/*МВЗ ПОЛУЧАТЕЛЬ 							*/"бу МВЗ (пол-ль)"
/*МВЗ 										*/@RELATIVE("№2. Доменный цех",0)
/*СЧЕТА БАЛАНСА 							*/"20_Начисление"
/*СЦЕНАРИЙ 									*/{СЦЕНАРИЙ}
/*ВЕРСИЯ 									*/{ВЕРСИЯ}
/*ГОД 										*/{ГОД}
/*ВИД ЗАТРАТ 								*/"Кокс влажный скиповой","Коксовый орех","Коксовая мелочь с печей","Коксовая мелочь с РВО","Коксовый орех с РВО","Агломерат скиповый", "Отсев агломерата"
/*СТАТЬИ 									*/@RELATIVE("ЦЕХОВАЯ СЕБЕСТОИМОСТЬ",0)
/*ПЕРИОД 									*/@RELATIVE("Итого год",0)
/*ПОКАЗАТЕЛИ 								*/
)
SET CREATENONMISSINGBLK ON;
"Кол-во"(
IF(@ISMBR("Коксовый орех с РВО"))
    ("Коксовый орех с РВО"->"ОПР Доменный цех"->"бу контрагента"->"бу направления"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"бу элемента затрат"->"Топливо."->"10_Поступление"->"бу МВЗ (пол-ль)"->"Кол-во"->"RUR"
    -"Коксовый орех с РВО"->"ОПР Доменный цех"->"бу контрагента"->"бу направления"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"бу элемента затрат"->"Топливо."->"10_Списание"->"бу МВЗ (пол-ль)"->"Кол-во"->"RUR")
    *"Кол-во"->"Коксовый орех"
    /"Кол-во"->"Коксовый орех"->"Чугун"->"Доменные печи";
ENDIF)
SET CREATENONMISSINGBLK OFF;

	/* Перенос цены  из формы 6.15.1.Кокс в калькуляции ДЦ */
	"Цена"(
	IF("Кол-во"+0!=0)
   		IF(@ISMBR("Кокс влажный скиповой","Коксовый орех"))
			"Топливо технологическое"->"ОПР Доменный цех"->"бу контрагента"->"бу направления"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"бу элемента затрат"->"бу статьи"->"10_Списание"->"бу МВЗ (пол-ль)"->"Цена."->"RUR";
        ELSEIF(@ISMBR("Коксовый орех с РВО"))
        "Топливо технологическое"->"ОПР Доменный цех"->"бу контрагента"->"бу направления"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"бу элемента затрат"->"бу статьи"->"10_Списание"->"бу МВЗ (пол-ль)"->"Цена."->"RUR"
        -"ОПР Доменный цех"->"бу контрагента"->"бу направления"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"бу элемента затрат"->"Топливо."->"10_Поступление"->"бу МВЗ (пол-ль)"->"Цена."->"RUR";
        ELSE
        	"ОПР Доменный цех"->"бу вида продукции"->"Прочее по ЭР"->"ПЭО"->"бу статьи"->"бу счета баланса"
            ;	
        ENDIF
	ENDIF
	)



	"Сумма"(
	IF("Цена"+0<>0 and "Кол-во"+0<>0)
		"Сумма"="Цена"*"Кол-во";
	ENDIF
	)
ENDFIX
