      ->"Прибыль/Убыток до налогообложения"->"Движения БДР"->"бу МВЗ (пол-ль)"->"Сумма"->"Рубль РФ"->"ПРОДУКЦИЯ ТЧМ и ТЧМС",
        @RELATIVE("ТЧМС итого",0));
        varIncomeSeb = "бу видов затрат"->"ТЧМС в целом"->"Расходы на производство"->"Итого по продукции"->"бу контрагента"->"бу направления"->"бу прочих справочников"->"ПЭО"->"Всего по первичным элементам затрат."->"Списание_себестоимости"->"бу МВЗ (пол-ль)"->"Сумма"->"Рубль (расчет)";

   IF (varIncomeTax > 0 AND "Ставка налога" <> #MISSING)
      "Налог на прибыль (расчет)" = (varIncomeTax - varIncomeSeb) * "Ставка налога"/100;
   ElSE
      "Налог на прибыль (расчет)"= #MISSING;
   ENDIF
)*/
"Налог на прибыль (расчет)"(
   varIncomeTax 
      = @SUMRANGE("ВИДЫ ЗАТРАТ ТЧМ И ТЧМС"->"Контрагенты ТЧМ и ТЧМС"->"Направления"
      ->"ПРОЧИЕ СПРАВОЧНИКИ ТЧМ и ТЧМС"->"Центры финансовой ответсвенности"->"ЭЛЕМЕНТЫ ЗАТРАТ ТЧМ И ТЧМС"
      ->"Прибыль/Убыток до налогообложения"->"Движения БДР"->"бу МВЗ (пол-ль)"->"Сумма"->"Рубль РФ"->"ПРОДУКЦИЯ ТЧМ и ТЧМС"->"ТЧМС итого",
        @LIST("январь":@CURRMBR("ПЕРИОД")));
      varIncomeSeb = @SUMRANGE("бу видов затрат"->"ТЧМС в целом"->"Расходы на производство"->"Итого по продукции"->"бу контрагента"->"бу направления"->"бу прочих справочников"->"ПЭО"
