   IF (varIncomeTax-varIncomeSeb > 0 AND "Ставка налога" <> #MISSING)
      "Налог на прибыль (расчет)" = @CREATEBLOCK("Ставка налога");
      "Налог на прибыль (расчет)" = (varIncomeTax-varIncomeSeb) * "Ставка налога"/100;
   ElSE
      "Налог на прибыль (расчет)"= #MISSING;
   ENDIF
)
ENDFIX

