/*Расчитываем форму*/

FIX(
/*ПРОЧЕЕ                         */"бу прочих справочников"
/*ПРОДУКЦИЯ                      */
/*НАПРАВЛЕНИЯ СПИСАНИЯ_ОТГРУЗКИ  */"бу направления"
/*ЭЛЕМЕНТЫ_ЗАТРАТ                */"бу элемента затрат"
/*ВАЛЮТА                         */"RUR"
/*ЦФО                            */"ПЭО"
/*СЧЕТА БАЛАНСА                  */
/*МВЗ ПОЛУЧАТЕЛЬ                 */"бу МВЗ (пол-ль)"
/*МВЗ                            */@RELATIVE({МВЗ},0) and @LIST("ТЧМ в целом","ТЧМС в целом")
/*СТАТЬИ                         */"Полуфабрикаты"
/*ВИД_ЗАТРАТ                     */"бу видов затрат"
/*ПОКАЗАТЕЛИ                     */
/*КОНТРАГЕНТЫ                    */"бу контрагента"
/*ВЕРСИЯ                         */{ВЕРСИЯ}
/*ГОД                            */{ГОД}
/*СЦЕНАРИЙ                       */{СЦЕНАРИЙ}
/*ПЕРИОД                         */@RELATIVE("Итого год",0)
)
    FIX(
    /*ПРОДУКЦИЯ                     */@RELATIVE("Заготовка стальная",0)     /*для ТЧМС*/
                                      @RELATIVE("Жидкая сталь на литье.",0) /*для ТЧМ*/
                                     "Патрубок газовый.","Фланец.","Рыло."  /*для ТЧМ*/
    )
        DATACOPY "21_Поступление"->"Сумма" to "21_Остаток на конец"->"Сумма";
        DATACOPY "21_Поступление"->"Кол-во" to "21_Остаток на конец"->"Кол-во";
    ENDFIX

    FIX(
    /*ПРОДУКЦИЯ                     */@RELATIVE("Заготовка стальная",0)     /*для ТЧМС*/
                                      @RELATIVE("Жидкая сталь на литье.",0) /*для ТЧМ*/
                                     "Патрубок газовый.","Фланец.","Рыло."  /*для ТЧМ*/
    )
        /*поступление равно выбытию */
        DATACOPY "21_Поступление"->"Сумма" to "21_Выбытие"->"Сумма";
        DATACOPY "21_Поступление"->"Кол-во" to "21_Выбытие"->"Кол-во";
    ENDFIX

    FIX(
    /*ПРОДУКЦИЯ                     */@RELATIVE("Заготовка стальная",0)     /*для ТЧМС*/
                                      @RELATIVE("Жидкая сталь на литье.",0) /*для ТЧМ*/
                                      "Патрубок газовый.","Фланец.","Рыло." /*для ТЧМ*/
    )    
        FIX(
        /*СЧЕТА БАЛАНСА                 */"21_Остаток на конец"
        )
            "Сумма" = #MISSING;
            "Сумма" 
                = @SUMRANGE("21_Поступление"->"Сумма",@LIST("январь":@CURRMBR("ПЕРИОД")))
                - @SUMRANGE("21_Выбытие"->"Сумма",@LIST("январь":@CURRMBR("ПЕРИОД")))
                + "21_Остаток на конец"->"Сумма"->"P0";

            "Кол-во" = #MISSING;
            "Кол-во" 
                = @SUMRANGE("21_Поступление"->"Кол-во",@LIST("январь":@CURRMBR("ПЕРИОД")))
                - @SUMRANGE("21_Выбытие"->"Кол-во",@LIST("январь":@CURRMBR("ПЕРИОД")))
                + "21_Остаток на конец"->"Кол-во"->"P0";
        ENDFIX
    ENDFIX 
ENDFIX 

