)

"Кол-во"=#MISSING;
"Кол-во" = @SUMRANGE("43_Производство ГП"->"Кол-во",@LIST("январь":@CURRMBR("ПЕРИОД")))
      - @SUMRANGE("43_Выбытие"->"Кол-во",@LIST("январь":@CURRMBR("ПЕРИОД")))
      +"43_Остаток на конец"->"Кол-во"->"P0";
ENDFIX


FIX(
/*ПОКАЗАТЕЛИ            */"Сумма"
)
LOOP(12)
"43_Выбытие"(
         IF(@ISMBR("январь"))
         "43_Остаток на конец"->"P0"->"Сумма"/"Кол-во"->"43_Остаток на конец"->"P0"*"Кол-во"->"43_Выбытие";
         ELSE
            (@PRIOR("43_Остаток на конец"->"Сумма")+"43_Производство ГП"->"Сумма")/(@PRIOR("43_Остаток на конец"->"Кол-во")+"43_Производство ГП"->"Кол-во")*"Кол-во"->"43_Выбытие";
          ENDIF)

"43_Остаток на конец"(
         (@PRIOR("43_Остаток на конец")+"43_Производство ГП")/(@PRIOR("43_Остаток на конец"->"Кол-во")+"43_Производство ГП"->"Кол-во")*"Кол-во";

            )

ENDLOOP                
ENDFIX 

