rule.script:
SET AGGMISSG ON;
SET UPDATECALC OFF;
SET CREATEBLOCKONEQ OFF;
SET CREATENONMISSINGBLK OFF;
SET FRMLBOTTOMUP OFF;
SET REMOTECALC OFF;
SET CALCPARALLEL 1;
SET CALCTASKDIMS 1;
SET EMPTYMEMBERSETS ON;
SET FRMLRTDYNAMIC OFF;
SET MSG INFO;
/* Проверка корректности рассчитываемого среза данных */

FIX(
/* ВАЛЮТА							*/	"бу валюты"
/* ПЕРИОД							*/	"P0"
/* ГОД								*/	"No Year"
/* ВЕРСИЯ							*/	
/* СЦЕНАРИЙ							*/	
/* ЦФО								*/	"бу ЦФО"
/* МВЗ								*/	"бу МВЗ"
/* МВЗ ПОЛУЧАТЕЛЬ					*/	"бу МВЗ (пол-ль)"
/* СЧЕТА БАЛАНСА					*/	"80_Увеличение"
/* СТАТЬИ							*/	
/* ПОКАЗАТЕЛИ						*/	"Сумма"
/* ВИД ЗАТРАТ						*/	"бу видов затрат"
/* ПРОДУКЦИЯ						*/	"бу вида продукции"
/* КОНТРАГЕНТЫ						*/	"бу контрагента"
/* НАПРАВЛЕНИЕ	*/	"бу направления"
/* ЭЛЕМЕНТЫ_ЗАТРАТ					*/	"бу элемента затрат"
/* ПРОЧЕЕ							*/	"бу прочих справочников"

)
  SET CREATENONMISSINGBLK ON;


  var ErrFlagScenario=0;
  var ErrFlagVersion=0;
  var NextM=2;
  /* ПРОВЕРКА КОРРЕКТНОСТИ ВЫБОРА СЦЕНАРИЯ */
  FIX(/* Сценарий */ {СЦЕНАРИЙ} AND @LIST(&ОТКРЫТЫЕ_СЦЕНАРИИ))
    FIX(
    /* Сценарий */ 
    /* Версия   */ "Рабочая 1"
    )
     "бу статьи" (
     IF (ErrFlagScenario==0)
            ErrFlagScenario=1;
     ENDIF
     );

    ENDFIX
  ENDFIX

   /* ПРОВЕРКА КОРРЕКТНОСТИ ВЫБОРА ВЕРСИИ */
  FIX(/* Версия  */ {ВЕРСИЯ} AND @LIST(&ОТКРЫТЫЕ_ВЕРСИИ))
    FIX(
    /* Сценарий */ "Проект"
    /* Версия   */ 
    )
     "бу статьи" (
     IF (ErrFlagVersion==0)
            ErrFlagVersion=1;
     ENDIF
     );
    ENDFIX
  ENDFIX

  /* ВЫВОД СООБЩЕНИЙ О НЕКОРРЕКТНОМ СРЕЗЕ */
  FIX(
  /* Сценарий     */ "Проект"
  /* Версия       */ "Рабочая 1"
  )

   "бу статьи" (
    IF (ErrFlagScenario==0)
       @RETURN("Выбран сценарий, закрытый для расчета. Расчет не произведен.",ERROR);
    ENDIF
    IF (ErrFlagVersion==0)
       @RETURN("Выбрана версия, закрытая для расчета. Расчет не произведен.",ERROR);
    ENDIF
    )

   ENDFIX
ENDFIX
SET CREATENONMISSINGBLK OFF;
FIX                            
(/*ВИД ЗАТРАТ                        */"бу видов затрат"
/*МВЗ                                */
/*ПРОДУКЦИЯ                          */"бу вида продукции"
/*МВЗ ПОЛУЧАТЕЛЬ                     */"бу МВЗ (пол-ль)"
/*ПОКАЗАТЕЛИ                         */"Сумма"
/*СТАТЬИ                             */"бу статьи"
/*ЦФО                                */"Финансовый отд"
/*ЭЛЕМЕНТЫ_ЗАТРАТ                    */"бу элемента затрат"
/*ВЕРСИЯ                             */{ВЕРСИЯ}
/*ГОД                                */{ГОД}
/*НАПРАВЛЕНИЕ      */@RELATIVE("Всего по срочности",0)
/*ПРОЧЕЕ                             */@RELATIVE("Всего по обязательствам",0)
/*ПЕРИОД                             */@RELATIVE("Итого год",0)
/*СЦЕНАРИЙ                           */{СЦЕНАРИЙ}
/*КОНТРАГЕНТЫ                        */@RELATIVE("Всего по контрагентам",0)
/*ВАЛЮТА                             */
/*СЧЕТА БАЛАНСА                      */
)

FIX(
/*МВЗ                                */(@LIST("ТЧМ в целом","ТЧМС в целом") and @RELATIVE({МВЗ},0))
)
FIX( 
/*СЧЕТА БАЛАНСА                       */"66_Ост на начало","66_Получение","66_Погашение","66_Курс разницы (+/-)","66_Рекласс по срочности (+/-)"

)
"Рубль (расчет)";
ENDFIX

/* akhodev - Добавил расчёт остатка на начало как значение остатка на конец из предыдущего месяца */
FIX(
/*ВАЛЮТА                             */@RELATIVE("Валюты ввода",0),"Рубль (расчет)"
)
"66_Ост на начало"(
IF(@ISMBR("Февраль":"Декабрь"))

"66_Ост на начало" = @PRIOR("66_Ост на начало") + @PRIOR("66_Получение") - @PRIOR("66_Погашение") + @PRIOR("66_Курс разницы (+/-)") + @PRIOR("66_Рекласс по срочности (+/-)");

ENDIF;
)
ENDFIX

FIX(
/*ВАЛЮТА                             */@RELATIVE("Валюты ввода",0),"Рубль (расчет)"
)
"66_Ост на конец"(
IF("66_Ост на начало"+"66_Получение"-"66_Погашение"+"66_Курс разницы (+/-)"+"66_Рекласс по срочности (+/-)"
<>#missing)

"66_Ост на конец"=@CREATEBLOCK("66_Ост на начало");
"66_Ост на конец"=@CREATEBLOCK("66_Получение");
"66_Ост на конец"=@CREATEBLOCK("66_Погашение");
"66_Ост на конец"=@CREATEBLOCK("66_Курс разницы (+/-)");
"66_Ост на конец"=@CREATEBLOCK("66_Рекласс по срочности (+/-)");


"66_Ост на конец"="66_Ост на начало"+"66_Получение"-"66_Погашение"+"66_Курс разницы (+/-)"+"66_Рекласс по срочности (+/-)";
ELSE

"66_Ост на конец"=#missing;

ENDIF
)
ENDFIX
ENDFIX
ENDFIX
